INSERT INTO `skveez_site_plugins` VALUES ('2','','','0','0','0','if(file_exists($modx->config[\'base_path\'].\'assets/plugins/\'.$folderPlugin.\'/lang/\'.$modx->config[\'manager_language\'].\'.inc.php\')){\r\n  include($modx->config[\'base_path\'].\'assets/plugins/\'.$folderPlugin.\'/lang/\'.$modx->config[\'manager_language\'].\'.inc.php\');\r\n}else{\r\n  include($modx->config[\'base_path\'].\'assets/plugins/\'.$folderPlugin.\'/lang/english.inc.php\');\r\n}\r\ninclude($modx->config[\'base_path\'].\'assets/plugins/\'.$folderPlugin.\'/version.class.php\');\r\nswitch($modx->Event->name){\r\n	/** Template */\r\n	case \'OnTempFormDelete\':{\r\n		$Diff=new ElementVer($modx,\'template\',$folderPlugin);\r\n		if($Diff->ignored($ignoredTPL)){\r\n			$Diff->del($modx->Event->params[\'id\']);\r\n		}\r\n		break;\r\n	}\r\n	case \'OnTempFormSave\':{\r\n		$Diff=new ElementVer($modx,\'template\',$folderPlugin);\r\n		if($Diff->ignored($ignoredTPL)){\r\n			$Diff->countVer=(int)$countTPL;\r\n			$Diff->save($modx->Event->params[\'id\'],\'post\');\r\n		}\r\n		break;\r\n	}\r\n	case \'OnTempFormRender\':{\r\n		$Diff=new ElementVer($modx,\'template\',$folderPlugin);\r\n		if($Diff->ignored($ignoredTPL)){\r\n			$out=$Diff->loadJs($idBlock,$which_jquery,$jqname,$js_src_type);\r\n			$modx->Event->output($out);\r\n		}\r\n		break;\r\n	}\r\n	\r\n	/** Snippet */\r\n	case \'OnSnipFormDelete\':{\r\n		$Diff=new ElementVer($modx,\'snippet\',$folderPlugin);\r\n		if($Diff->ignored($ignoredSnippet)){\r\n			$Diff->del($modx->Event->params[\'id\']);\r\n		}\r\n		break;\r\n	}\r\n	case \'OnSnipFormSave\':{\r\n		$Diff=new ElementVer($modx,\'snippet\',$folderPlugin);\r\n		if($Diff->ignored($ignoredSnippet)){\r\n			$Diff->countVer=(int)$countSnippet;\r\n			$Diff->save($modx->Event->params[\'id\'],\'post\');\r\n		}\r\n		break;\r\n	}\r\n	case \'OnSnipFormRender\':{\r\n		$Diff=new ElementVer($modx,\'snippet\',$folderPlugin);\r\n		if($Diff->ignored($ignoredSnippet)){\r\n			$out=$Diff->loadJs($idBlock,$which_jquery,$jqname,$js_src_type);\r\n			$modx->Event->output($out);\r\n		}\r\n		break;\r\n	}\r\n	\r\n	/** Plugin */\r\n	case \'OnPluginFormDelete\':{\r\n		$Diff=new ElementVer($modx,\'plugin\',$folderPlugin);\r\n		if($Diff->ignored($ignoredPlugin)){\r\n			$Diff->del($modx->Event->params[\'id\']);\r\n		}\r\n		break;\r\n	}\r\n	case \'OnPluginFormSave\':{\r\n		$Diff=new ElementVer($modx,\'plugin\',$folderPlugin);\r\n		if($Diff->ignored($ignoredPlugin)){\r\n			$Diff->countVer=(int)$countPlugin;\r\n			$Diff->save($modx->Event->params[\'id\'],\'post\');\r\n		}\r\n		break;\r\n	}\r\n	case \'OnPluginFormRender\':{\r\n		$Diff=new ElementVer($modx,\'plugin\',$folderPlugin);\r\n		if($Diff->ignored($ignoredPlugin)){\r\n			$out=$Diff->loadJs($idBlock,$which_jquery,$jqname,$js_src_type);\r\n			$modx->Event->output($out);\r\n		}\r\n		break;\r\n	}\r\n	\r\n	/** Module */\r\n	case \'OnModFormDelete\':{\r\n		$Diff=new ElementVer($modx,\'module\',$folderPlugin);\r\n		if($Diff->ignored($ignoredModule)){\r\n			$Diff->del($modx->Event->params[\'id\']);\r\n		}\r\n		break;\r\n	}\r\n	case \'OnModFormSave\':{\r\n		$Diff=new ElementVer($modx,\'module\',$folderPlugin);\r\n		if($Diff->ignored($ignoredModule)){\r\n			$Diff->countVer=(int)$countModule;\r\n			$Diff->save($modx->Event->params[\'id\'],\'post\');\r\n		}\r\n		break;\r\n	}\r\n	case \'OnModFormRender\':{\r\n		$Diff=new ElementVer($modx,\'module\',$folderPlugin);\r\n		if($Diff->ignored($ignoredModule)){\r\n			$out=$Diff->loadJs($idBlock,$which_jquery,$jqname,$js_src_type);\r\n			$modx->Event->output($out);\r\n		}\r\n		break;\r\n	}\r\n	\r\n	/** Chunk */\r\n	case \'OnChunkFormDelete\':{\r\n		$Diff=new ElementVer($modx,\'chunk\',$folderPlugin);\r\n		if($Diff->ignored($ignoredChunk)){\r\n			$Diff->del($modx->Event->params[\'id\']);\r\n		}\r\n		break;\r\n	}\r\n	case \'OnChunkFormSave\':{\r\n		$Diff=new ElementVer($modx,\'chunk\',$folderPlugin);\r\n		if($Diff->ignored($ignoredChunk)){\r\n			$Diff->countVer=(int)$countChunk;\r\n			$Diff->save($modx->Event->params[\'id\'],\'post\');\r\n		}\r\n		break;\r\n	}\r\n	case \'OnChunkFormRender\':{\r\n		$Diff=new ElementVer($modx,\'chunk\',$folderPlugin);\r\n		if($Diff->ignored($ignoredChunk)){\r\n			$out=$Diff->loadJs($idBlock,$which_jquery,$jqname,$js_src_type);\r\n			$modx->Event->output($out);\r\n		}\r\n		break;\r\n	}\r\n	/** Document */\r\n	case \'OnDocFormDelete\':{\r\n		$Diff=new ElementVer($modx,\'document\',$folderPlugin);\r\n		if($Diff->ignored($ignoredDoc)){\r\n			$Diff->del($modx->Event->params[\'id\']);\r\n		}\r\n		break;\r\n	}\r\n	case \'OnDocFormSave\':{\r\n		$Diff=new ElementVer($modx,\'document\',$folderPlugin);\r\n		if($Diff->ignored($ignoredDoc)){\r\n			$Diff->countVer=(int)$countDoc;\r\n			$Diff->save($modx->Event->params[\'id\'],\'ta\');\r\n		}\r\n		break;\r\n	}\r\n	case \'OnDocFormRender\':{\r\n		$Diff=new ElementVer($modx,\'document\',$folderPlugin);\r\n		if($Diff->ignored($ignoredDoc)){\r\n			$out=$Diff->loadJs($idBlock,$which_jquery,$jqname,$js_src_type);\r\n			$modx->Event->output($out);\r\n		}\r\n		break;\r\n	}\r\n}\r\n//?>','0','','1','');
INSERT INTO `skveez_site_plugins` VALUES ('3','Search Highlight','<strong>1.5</strong> Used with AjaxSearch to show search terms highlighted on page linked from search results','0','0','0','/*\r\n  ------------------------------------------------------------------------\r\n  Plugin: Search_Highlight v1.5\r\n  ------------------------------------------------------------------------\r\n  Changes:\r\n  18/03/10 - Remove possibility of XSS attempts being passed in the URL\r\n           - look-behind assertion improved\r\n  29/03/09 - Removed urldecode calls;\r\n           - Added check for magic quotes - if set, remove slashes\r\n           - Highlights terms searched for when target is a HTML entity\r\n  18/07/08 - advSearch parameter and pcre modifier added\r\n  10/02/08 - Strip_tags added to avoid sql injection and XSS. Use of $_REQUEST\r\n  01/03/07 - Added fies/updates from forum from users mikkelwe/identity\r\n  (better highlight replacement, additional div around term/removal message)\r\n  ------------------------------------------------------------------------\r\n  Description: When a user clicks on the link from the AjaxSearch results\r\n    the target page will have the terms highlighted.\r\n  ------------------------------------------------------------------------\r\n  Created By:  Susan Ottwell (sottwell@sottwell.com)\r\n               Kyle Jaebker (kjaebker@muddydogpaws.com)\r\n\r\n  Refactored by Coroico (www.modx.wangba.fr) and TS\r\n  ------------------------------------------------------------------------\r\n  Based off the the code by Susan Ottwell (www.sottwell.com)\r\n    http://modxcms.com/forums/index.php/topic,1237.0.html\r\n  ------------------------------------------------------------------------\r\n  CSS:\r\n    The classes used for the highlighting are the same as the AjaxSearch\r\n  ------------------------------------------------------------------------\r\n  Notes:\r\n    To add a link to remove the highlighting and to show the searchterms\r\n    put the following on your page where you would like this to appear:\r\n\r\n      <!--search_terms-->\r\n\r\n    Example output for this:\r\n\r\n      Search Terms: the, template\r\n      Remove Highlighting\r\n\r\n    Set the following variables to change the text:\r\n\r\n      $termText - the text before the search terms\r\n      $removeText - the text for the remove link\r\n  ------------------------------------------------------------------------\r\n*/\r\nglobal $database_connection_charset;\r\n// Conversion code name between html page character encoding and Mysql character encoding\r\n// Some others conversions should be added if needed. Otherwise Page charset = Database charset\r\n$pageCharset = array(\r\n  \'utf8\' => \'UTF-8\',\r\n  \'latin1\' => \'ISO-8859-1\',\r\n  \'latin2\' => \'ISO-8859-2\'\r\n);\r\n\r\nif (isset($_REQUEST[\'searched\']) && isset($_REQUEST[\'highlight\'])) {\r\n\r\n  // Set these to customize the text for the highlighting key\r\n  // --------------------------------------------------------\r\n     $termText = \'<div class=\"searchTerms\">Search Terms: \';\r\n     $removeText = \'Remove Highlighting\';\r\n  // --------------------------------------------------------\r\n\r\n  $highlightText = $termText;\r\n  $advsearch = \'oneword\';\r\n\r\n  $dbCharset = $database_connection_charset;\r\n  $pgCharset = array_key_exists($dbCharset,$pageCharset) ? $pageCharset[$dbCharset] : $dbCharset;\r\n\r\n  // magic quotes check\r\n  if (get_magic_quotes_gpc()){\r\n    $searched = strip_tags(stripslashes($_REQUEST[\'searched\']));\r\n    $highlight = strip_tags(stripslashes($_REQUEST[\'highlight\']));\r\n    if (isset($_REQUEST[\'advsearch\'])) $advsearch = strip_tags(stripslashes($_REQUEST[\'advsearch\']));\r\n  }\r\n  else {\r\n    $searched = strip_tags($_REQUEST[\'searched\']);\r\n    $highlight = strip_tags($_REQUEST[\'highlight\']);\r\n    if (isset($_REQUEST[\'advsearch\'])) $advsearch = strip_tags($_REQUEST[\'advsearch\']);\r\n  }\r\n\r\n  if ($advsearch != \'nowords\') {\r\n\r\n    $searchArray = array();\r\n    if ($advsearch == \'exactphrase\') $searchArray[0] = $searched;\r\n    else $searchArray = explode(\' \', $searched);\r\n\r\n    $searchArray = array_unique($searchArray);\r\n    $nbterms = count($searchArray);\r\n    $searchTerms = array();\r\n    for($i=0;$i<$nbterms;$i++){\r\n      // Consider all possible combinations\r\n      $word_ents = array();\r\n      $word_ents[] = $searchArray[$i];\r\n      $word_ents[] = htmlentities($searchArray[$i], ENT_NOQUOTES, $pgCharset);\r\n      $word_ents[] = htmlentities($searchArray[$i], ENT_COMPAT, $pgCharset);\r\n      $word_ents[] = htmlentities($searchArray[$i], ENT_QUOTES, $pgCharset);\r\n      // Avoid duplication\r\n      $word_ents = array_unique($word_ents);\r\n      foreach($word_ents as $word) $searchTerms[]= array(\'term\' => $word, \'class\' => $i+1);\r\n    }\r\n\r\n    $output = $modx->documentOutput; // get the parsed document\r\n    $body = explode(\"<body\", $output); // break out the head\r\n\r\n    $highlightClass = explode(\' \',$highlight); // break out the highlight classes\r\n    /* remove possibility of XSS attempts being passed in URL */\r\n    foreach ($highlightClass as $key => $value) {\r\n       $highlightClass[$key] = preg_match(\'/[^A-Za-z0-9_-]/ms\', $value) == 1 ? \'\' : $value;\r\n    }\r\n\r\n    $pcreModifier = ($pgCharset == \'UTF-8\') ? \'iu\' : \'i\';\r\n    $lookBehind = \'/(?<!&|&[\\w#]|&[\\w#]\\w|&[\\w#]\\w\\w|&[\\w#]\\w\\w\\w|&[\\w#]\\w\\w\\w\\w|&[\\w#]\\w\\w\\w\\w\\w)\';  // avoid a match with a html entity\r\n    $lookAhead = \'(?=[^>]*<)/\'; // avoid a match with a html tag\r\n\r\n    $nbterms = count($searchTerms);\r\n    for($i=0;$i<$nbterms;$i++){\r\n      $word = $searchTerms[$i][\'term\'];\r\n      $class = $highlightClass[0].\' \'.$highlightClass[$searchTerms[$i][\'class\']];\r\n\r\n      $highlightText .= ($i > 0) ? \', \' : \'\';\r\n      $highlightText .= \'<span class=\"\'.$class.\'\">\'.$word.\'</span>\';\r\n\r\n      $pattern = $lookBehind . preg_quote($word, \'/\') . $lookAhead . $pcreModifier;\r\n      $replacement = \'<span class=\"\' . $class . \'\">${0}</span>\';\r\n      $body[1] = preg_replace($pattern, $replacement, $body[1]);\r\n    }\r\n\r\n    $output = implode(\"<body\", $body);\r\n\r\n    $removeUrl = $modx->makeUrl($modx->documentIdentifier);\r\n    $highlightText .= \'<br /><a href=\"\'.$removeUrl.\'\" class=\"ajaxSearch_removeHighlight\">\'.$removeText.\'</a></div>\';\r\n\r\n    $output = str_replace(\'<!--search_terms-->\',$highlightText,$output);\r\n    $modx->documentOutput = $output;\r\n  }\r\n}','0','','1','');
INSERT INTO `skveez_site_plugins` VALUES ('4','EvoGallery','<strong>1.1 Beta 1</strong> Plugin for delete images on empty trash','0','0','0','if (!isset($params[\'modulePath\'])) $params[\'modulePath\'] = $modx->config[\'base_path\'].\'assets/modules/evogallery/\';\r\ninclude_once($params[\'modulePath\'] . \"classes/management.class.inc.php\");\r\nif (class_exists(\'GalleryManagement\'))\r\n	$manager = new GalleryManagement($params);\r\nelse\r\n	$modx->logEvent(1, 3, \'Error loading Portfolio Galleries management module\');\r\n$e =& $modx->event;\r\nswitch ($e->name ) {\r\n    case \'OnEmptyTrash\':\r\n		$manager->deleteImages(\'contentid\',$ids);\r\n		break ;\r\n    default:\r\n        return ;\r\n}\r\n','0','','1','23636a8c613426979b9dea1ff0415abf');
INSERT INTO `skveez_site_plugins` VALUES ('5','EasyAdvertising','<strong>1.02</strong> плагин для управления рекламой на сайте','0','0','0','/*\r\n*  Easy Advertising -                      \r\n*  плагин для управления рекламой на сайте \r\n*  Версия 1.02\r\n*  Автор Леха.com\r\n*  mod. lo-pata (s.vlksm@gmail.com)  \r\n*  вешать на событие OnParseDocument\r\n*/\r\nif (count($modx->ui)) {\r\n    $sql = \"UPDATE \".$modx->getFullTableName(\"site_easyadvt\").\" SET count_view = count_view + 1 WHERE id IN (\".implode(\',\',$modx->ui).\")\";\r\n    $modx->db->query($sql);\r\n    $modx->ui = array();\r\n}','0','','1','');
INSERT INTO `skveez_site_plugins` VALUES ('6','CacheAcceleratorPlugin','<strong>0.4b</strong> Clear CacheAccelerator cache files and managing autocache','0','0','0','if(!function_exists(\'processInjection\')){\r\n    function processInjection($str, $autoCacheSnippets, $autoCacheExcludeSnippets, $dropCacheFields, $showSnippets){\r\n        $pos = 0;\r\n        while($pos = strpos($str, \'[!\', $pos)){\r\n            if($pos1 = strpos($str, \'!]\', $pos)){\r\n                $pos3 = $pos1;\r\n                $pos2 = strpos($str, \'?\', $pos);\r\n                if($pos2 && $pos1 > $pos2){\r\n                    $pos1 = $pos2;\r\n                    $offs = 1;\r\n                } else\r\n                    $offs = 0;\r\n                $pos+=2;\r\n                $snippetToCache = trim(substr($str, $pos, $pos1 - $pos));\r\n                $symb = strtolower($snippetToCache[0]);\r\n                if($symb >= \'a\' && $symb <= \'z\' && strtolower($snippetToCache) != \'cacheaccelerator\'){\r\n                    if(!is_null($autoCacheSnippets)){\r\n                        if(!in_array($snippetToCache, $autoCacheSnippets)){\r\n                            continue;\r\n                        }\r\n                    }\r\n                    if(!is_null($autoCacheExcludeSnippets)){\r\n                        if(in_array($snippetToCache, $autoCacheExcludeSnippets)){\r\n                            continue;\r\n                        }\r\n                    }\r\n                    $dcfStr = ($dropCacheFields != \'\') ? \' &dropCacheField=`\'.str_replace(\',\', \';\', $dropCacheFields).\'`\' : \'\';\r\n                    $logMessages = ($showSnippets != \'no\') ? \' &logMessages=`1`\' : \'\';\r\n                    if($showSnippets == \'short\')\r\n                        echo(\'CacheAccelerator Caching: \'.$snippetToCache.\'<br />\');\r\n                    if($showSnippets == \'full\')\r\n                        echo(\'CacheAccelerator Caching: \'.$snippetToCache.\', Call: [!CacheAccelerator? &snippetToCache=`\'.$snippetToCache.\'`\'.$logMessages.\' &cacheId=`\'.md5(substr($str, $pos, $pos3 - $pos)).\'`\'.$dcfStr.substr($str, $pos1, $pos3 - $pos1).\'!]<br /><br />\');\r\n\r\n                    $str = substr($str, 0, $pos).\'CacheAccelerator? &snippetToCache=`\'.$snippetToCache.\'`\'.$logMessages.\' &cacheId=`\'.md5(substr($str, $pos, $pos3 - $pos)).\'`\'.$dcfStr.substr($str, $pos1 + $offs);\r\n                }\r\n            }\r\n        }\r\n        return $str;\r\n    }\r\n}\r\n\r\n$only_manual = isset($only_manual) ? $only_manual : \'no\';\r\n$e = &$modx->Event;\r\n\r\nif($e->name == \'OnWebPagePrerender\'){\r\n    if($showSystemData == \'yes\'){\r\n//        if($modx->checkSession()){\r\n\r\n            $out = \"<div style=\'clear:both\'>&nbsp;</div>MySQL: [^qt^], [^q^] request(s), PHP: [^p^], total: [^t^], Memory: [^m^], document retrieved from [^s^]. \";\r\n            $totalTime= ($modx->getMicroTime() - $modx->tstart);\r\n            $queryTime= $modx->queryTime;\r\n            $phpTime= $totalTime - $queryTime;\r\n\r\n            $queryTime= sprintf(\"%2.4f s\", $queryTime);\r\n            $totalTime= sprintf(\"%2.4f s\", $totalTime);\r\n            $phpTime= sprintf(\"%2.4f s\", $phpTime);\r\n            $phpMemory = (memory_get_peak_usage(true) / 1024 / 1024) . \" MB\";\r\n            $source= $modx->documentGenerated == 1 ? \"database\" : \"cache\";\r\n            $queries= isset ($modx->executedQueries) ? $modx->executedQueries : 0;\r\n            $out= str_replace(\"[^m^]\", $phpMemory, $out);\r\n            $out= str_replace(\"[^q^]\", $queries, $out);\r\n            $out= str_replace(\"[^qt^]\", $queryTime, $out);\r\n            $out= str_replace(\"[^p^]\", $phpTime, $out);\r\n            $out= str_replace(\"[^t^]\", $totalTime, $out);\r\n            $out= str_replace(\"[^s^]\", $source, $out);\r\n            $this->documentOutput .= $out;\r\n//        }\r\n    }\r\n    return;\r\n}\r\n\r\nif($autoCache == \'enabled\' && $e->name == \'OnLoadWebDocument\'){\r\n    if($autoCacheSnippets != \'\' && $autoCacheSnippets != \'all\'){\r\n        $autoCacheSnippets = trim(preg_replace(\'|\\\\s*(?:\'.preg_quote(\',\').\')\\\\s*|\', \',\', $autoCacheSnippets));\r\n        $autoCacheSnippets = explode(\",\", $autoCacheSnippets);\r\n    } else\r\n        $autoCacheSnippets = null;\r\n    if($autoCacheExcludeSnippets != \'\'){\r\n        $autoCacheExcludeSnippets = trim(preg_replace(\'|\\\\s*(?:\'.preg_quote(\',\').\')\\\\s*|\', \',\', $autoCacheExcludeSnippets));\r\n        $autoCacheExcludeSnippets = explode(\",\", $autoCacheExcludeSnippets);\r\n    } else\r\n        $autoCacheExcludeSnippets = null;\r\n    foreach($modx->chunkCache as $key => $chunk)\r\n        $modx->chunkCache[$key] = processInjection($chunk, $autoCacheSnippets, $autoCacheExcludeSnippets, $dropCacheFields, $showSnippets);\r\n    $modx->documentContent = processInjection($modx->documentContent, $autoCacheSnippets, $autoCacheExcludeSnippets, $dropCacheFields, $showSnippets);\r\n    $modx->documentObject[\'content\'] = processInjection($modx->documentObject[\'content\'], $autoCacheSnippets, $autoCacheExcludeSnippets, $dropCacheFields, $showSnippets);\r\n    return;\r\n}\r\n\r\nif (($e->name == \'OnCacheUpdate\' && $only_manual==\'no\') || ($e->name == \'OnSiteRefresh\' && $only_manual==\'yes\')) {\r\n    $path_to_cacheengine = $modx->config[\'base_path\'].\"assets/plugins/cacheaccelerator/\";\r\n    $path_to_cache = $modx->config[\'base_path\'].\"assets/plugins/cacheaccelerator/cache/\";\r\n    require_once $path_to_cacheengine.\"fileCache.php\";\r\n    $cache = fileCache::GetInstance(84600*7,$path_to_cache);\r\n    if($dropCacheGroups != \'\' && $dropCacheGroups != \'all\'){\r\n        $dropCacheGroups = trim(preg_replace(\'|\\\\s*(?:\'.preg_quote(\',\').\')\\\\s*|\', \',\', $dropCacheGroups));\r\n        $dropCacheGroups = explode(\",\", $dropCacheGroups);\r\n        $cache->deleteCache(0, $dropCacheGroups);\r\n    } else\r\n        $cache->deleteCache(0);\r\n    return;\r\n}\r\n\r\n','0','&only_manual=Only manual clear;list;yes,no;no &autoCache=Autocache;list;enabled,disabled;disabled &autoCacheSnippets=Snippets to Autocache (\'ditto,jot\');string;all &autoCacheExcludeSnippets=Snippets to exclude (\'snippet1, snippet2\');string;include &dropCacheGroups=Groups to clear (\'default, news\');string;all &dropCacheFields=Drop cache fields;string;JotForm||post,true,2||,publish,2||,unpublish,2||,delete,2||,edit,2 &showSnippets=Show snippets log;list;full,short,no;no &showSystemData=Show system data;list;yes,no;no','1','');
INSERT INTO `skveez_site_plugins` VALUES ('7','MultiPhotos','<strong>1.26</strong> Добавление нескольких фотографий к странице','0','0','0','///defined(\'IN_MANAGER_MODE\') or die();\r\n\r\nglobal $content,$default_template,$tmplvars;\r\n$tvIds = isset($tvIds) ? $tvIds : 111;\r\n$w = isset($w) ? $w : 160;\r\n$h = isset($h) ? $h : 120;\r\n$templ = isset($templ) ? explode(\',\',$templ) : false;\r\n$role = isset($role) ? explode(\',\',$role) : false;\r\n$style = (isset($w) || isset($h)) ? \"\'max-width\':\'{$w}px\',\'max-height\':\'{$h}px\',\'cursor\':\'pointer\'\" : \'\';\r\n$site = $modx->config[\'site_url\'];\r\n$thumbUrl = isset($thumbUrl) ? \'url = (url != \"\") ? (\"\'.$thumbUrl.\'?src=\"+escape(url)+\"&w=\'.$w.\'&h=\'.$h.\'\") : url; \' : \'url = (url != \"\" && url.search(/http:\\/\\//i) == -1) ? (\"\'.$site.\'\" + url) : url;\';\r\n$cur_templ = isset($_POST[\'template\']) ? $_POST[\'template\'] : (isset($content[\'template\']) ? $content[\'template\'] : $default_template);\r\n$cur_role = $_SESSION[\'mgrRole\'];\r\nif (($templ && !in_array($cur_templ,$templ)) || ($role && !in_array($cur_role,$role))) return;\r\n\r\n$resize = isset($resize)&&($resize==\'true\') ? 1 : 0;\r\n$crop = isset($crop)&&($crop==\'true\') ? 1 : 0;\r\n$prefix = isset($prefix) ? $prefix : \'s_\';\r\n$auto_big = isset($auto_big)&&($auto_big==\'true\') ? 1 : 0;\r\n$auto_small = isset($auto_small)&&($auto_small==\'true\') ? 1 : 0;\r\n\r\n$lang[\'insert\']=\'Вставить\';\r\n$lang[\'url\']=\'Путь:\';\r\n$lang[\'link\']=\'Ссылка или большая картинка:\';\r\n$lang[\'title\']=\'Название:\';\r\n\r\n$e = &$modx->Event;\r\nif ($e->name == \'OnDocFormRender\') {\r\nrequire_once(MODX_MANAGER_PATH.\'includes/tmplvars.inc.php\');\r\n$modx_script = renderFormElement(\'image\',0,\'\',\'\',\'\');\r\npreg_match(\'/(<script[^>]*?>.*?<\\/script>)/si\', $modx_script, $matches);\r\n$output = $matches ? $matches[0] : \'\';\r\n$output .= <<< OUT\r\n<!-- MultiPhotos -->\r\n<style type=\"text/css\">\r\n.fotoitem {border:1px solid #e3e3e3; margin:0 0 5px; padding:2px 5px 5px 5px; position:relative; overflow:hidden; white-space:nowrap; zoom:1}\r\n.fotoitem span {display:inline-block; padding-top:3px;}\r\n.fotoitem input {line-height:1.1; vertical-align:middle;}\r\n.fotoimg {position:absolute; right:0; padding-top:3px;}\r\n</style>\r\n<script type=\"text/javascript\">\r\nwindow.ie9=window.XDomainRequest && window.performance; window.ie=window.ie && !window.ie9; /* IE9 patch */\r\nvar MultiPhotos = new Class({\r\n	initialize: function(fid){\r\n		this.name = fid;\r\n		this.fid = $(fid);\r\n		var hpArr = (this.fid.value && this.fid.value!=\'[]\') ? Json.evaluate(this.fid.value) : [null];\r\n		this.fid.setStyle(\'display\',\'none\');\r\n		this.box = new Element(\'div\',{\'class\':\'fotoEditor\'});\r\n		this.fid.getParent().adopt(this.box);\r\n		this.foto=0;\r\n		for (var f=0;f<hpArr.length;f++) this.addItem(hpArr[f]);\r\n		if (typeof(SetUrl) != \'undefined\') {\r\n			this.OrigSetUrl = SetUrl;				\r\n			SetUrl = function(url, width, height, alt) {\r\n				var lastfoto = lastImageCtrl;\r\n				this.OrigSetUrl(url, width, height, alt);\r\n				if ($(lastfoto)!=null) $(lastfoto).fireEvent(\'change\');\r\n			}.bind(this)\r\n		}\r\n		this.sort=new Sortables(this.box,{\r\n			onStart: function(el){el.setStyles({\'background\':\'#f0f0f0\',\'opacity\':1});},\r\n			onComplete: function(el){el.setStyle(\'background\',\'none\');this.setEditor();}.bind(this)\r\n		});	\r\n		this.box.getElements(\'div.fotoitem\').setStyle(\'cursor\',\'move\');\r\n		this.box.getElements(\'input[type=text]\').addEvent(\'click\',function(){this.focus();});\r\n	},\r\n	br: function(){return new Element(\'br\');},\r\n	sp: function(text){return new Element(\'span\').setText(text);},\r\n	addItem: function(values,elem){\r\n		this.foto++;\r\n		var f = this.foto;\r\n		var rowDiv = new Element(\'div\',{\'class\':\'fotoitem\'});\r\n		if (elem) {rowDiv.injectAfter(elem);} else {this.box.adopt(rowDiv);}\r\n		if (!values) values=[\'\',\'\',\'\']; \r\n		var imgURL = new Element(\'input\',{\'type\':\'text\',\'name\':\'foto_\'+this.name+\'_\'+f,\'id\':\'foto_\'+this.name+\'_\'+f,\'class\':\'imageField\',\'value\':values[0],\'events\':{\r\n			\'change\':function(){\r\n				var url = imgURL.value;\r\n				{$thumbUrl}\r\n				var imgDiv=$(\'foto_\'+this.name+\'_\'+f+\'_\'+\'PrContainer\');\r\n				if (imgDiv!=null) imgDiv.remove();\r\n				if (url != \"\") {\r\n					new Element(\'div\',{\'class\':\'fotoimg\',\'id\':\'foto_\'+this.name+\'_\'+f+\'_\'+\'PrContainer\',\'styles\':{\'width\':\'{$w}px\'}}).injectTop(rowDiv).adopt(\r\n						new Element(\'img\',{\'src\':url,\'styles\':{ $style },\'events\':{\r\n							\'click\':function(){BrowseServer(\'foto_\'+this.name+\'_\'+f)}.bind(this)\r\n						}})\r\n					);\r\n				}\r\n				this.setEditor();\r\n			}.bind(this)\r\n		}});\r\n		var bInsert = new Element(\'input\',{\'type\':\'button\',\'value\':\'{$lang[\'insert\']}\',\'events\':{\r\n			\'click\':function(){BrowseServer(\'foto_\'+this.name+\'_\'+f)}.bind(this)\r\n		}});\r\n		var linkURL = new Element(\'input\',{\'type\':\'text\',\'name\':\'link_\'+this.name+\'_\'+f,\'id\':\'link_\'+this.name+\'_\'+f,\'class\':\'imageField\',\'value\':values[1],\'events\':{\r\n			\'change\':function(){this.setEditor();}.bind(this)\r\n		}});\r\n		var bInsertLink = new Element(\'input\',{\'type\':\'button\',\'value\':\'{$lang[\'insert\']}\',\'events\':{\r\n			\'click\':function(){BrowseServer(\'link_\'+this.name+\'_\'+f)}.bind(this)\r\n		}});\r\n		var imgName = new Element(\'input\',{\'type\':\'text\',\'class\':\'imageField\',\'value\':values[2],\'events\':{\r\n			\'keyup\':function(){this.setEditor();documentDirty=true;}.bind(this)\r\n		}});\r\n		var bAdd = new Element(\'input\',{\'type\':\'button\',\'value\':\'+\',\'events\': {\r\n			\'click\':function(){this.addItem(null,rowDiv);}.bind(this)\r\n		}});\r\n		rowDiv.adopt(this.sp(\'{$lang[\'url\']}\'),this.br(),imgURL,bInsert,this.br(),this.sp(\'{$lang[\'link\']}\'),this.br(),linkURL,bInsertLink,this.br());\r\n		rowDiv.adopt(this.sp(\'{$lang[\'title\']}\'),this.br(),imgName,bAdd);\r\n		if (this.box.getElements(\'div.fotoitem\').length>1) rowDiv.adopt(new Element(\'input\',{\'type\':\'button\',\'value\':\'-\',\'events\':{\r\n			\'click\':function(){rowDiv.remove();this.setEditor();}.bind(this)\r\n		}}));\r\n		imgURL.fireEvent(\'change\');\r\n	},\r\n	setEditor: function(){\r\n		var hpArr=new Array();\r\n		this.box.getElements(\'div.fotoitem\').each(function(item){\r\n			var itemsArr=new Array();\r\n			var inputs=item.getElements(\'input[type=text]\');\r\n			var noempty=false;\r\n			inputs.each(function(item){itemsArr.push(item.value); if (item.value) noempty=true;});\r\n			if (noempty) hpArr.push(itemsArr);\r\n		});\r\n		this.fid.value = Json.toString(hpArr);\r\n	}\r\n});\r\nwindow.addEvent(\'domready\', function(){\r\n	var tvIds = [$tvIds];\r\n	for (var i=0;i<tvIds.length;i++){\r\n		var fid = \'tv\'+ tvIds[i];\r\n		if($(fid)!=null) {var modxMultiPhotos=new MultiPhotos(fid);}\r\n	}\r\n});\r\n</script>\r\n<!-- /MultiPhotos -->\r\nOUT;\r\n$e->output($output);\r\n}\r\nif ($e->name == \'OnBeforeDocFormSave\'){\r\n$tvIds=explode(\',\',$tvIds);\r\nforeach ($tvIds as $tvid) {\r\n	if (empty($th_width) && empty($th_height)) return;\r\n	if (!$resize || !isset($tmplvars[$tvid]) || empty($tmplvars[$tvid][1])) continue;\r\n	$fotoArr=json_decode($tmplvars[$tvid][1]);\r\n	@set_time_limit(0);\r\n	foreach ($fotoArr as $k=>&$v) {\r\n		if (!empty($v[1]) && $auto_small) $v[0]=$v[1];\r\n		if (!empty($v[0])){\r\n			$filename = basename($v[0]);\r\n			$dirname = str_replace($filename,\'\',$v[0]);\r\n			if (!($auto_small && !empty($v[1])) && ($prefix==substr($filename, 0, strlen($prefix)) || $prefix==substr($dirname, -strlen($prefix)))) continue;\r\n			$new_path = \'../\'.$dirname.$prefix.$filename;\r\n			$imgInfo = @getImageSize(\'../\'.$v[0]);\r\n			if (!is_array($imgInfo)) continue;\r\n			ob_start();\r\n			$img_width = $imgInfo[0];\r\n			$img_height = $imgInfo[1];\r\n			$width=$img_width;\r\n			$height=$img_height;\r\n			$posX=0;\r\n			$posY=0;	\r\n			$ratio = $img_height / $img_width;\r\n			if (!$th_height) $th_h=round($th_width*$ratio); else $th_h=$th_height;\r\n			if (!$th_width) $th_w=round($th_height/$ratio); else $th_w=$th_width;\r\n			$th_ratio = $th_h / $th_w;\r\n			if ($crop) {\r\n				if ($ratio > $th_ratio) {\r\n					$height=round($img_width*$th_ratio);\r\n					$posY=round(($img_height-$height)/2);\r\n				}\r\n				if ($ratio < $th_ratio) {\r\n					$width=round($img_height/$th_ratio);\r\n					$posX=round(($img_width-$width)/2);\r\n				}\r\n			}\r\n			else {\r\n				if ($ratio > $th_ratio) $th_w=round($th_h/$ratio);\r\n				if ($ratio < $th_ratio) $th_h=round($th_w*$ratio);\r\n			}\r\n			switch($imgInfo[2]){\r\n			case 1:\r\n				$src = ImageCreateFromGif(\'../\'.$v[0]);\r\n				$dst = ImageCreateTrueColor($th_w, $th_h);\r\n				ImageCopyResampled($dst, $src, 0, 0, $posX, $posY, $th_w, $th_h, $width, $height);\r\n				ImageGif($dst,$new_path);\r\n				break;\r\n			case 2:\r\n				$src = ImageCreateFromJpeg(\'../\'.$v[0]);\r\n				$dst = ImageCreateTrueColor($th_w, $th_h);\r\n				ImageCopyResampled($dst, $src, 0, 0, $posX, $posY, $th_w, $th_h, $width, $height);\r\n				ImageJpeg($dst,$new_path,90);\r\n				break;\r\n			case 3:\r\n				$src = ImageCreateFromPng(\'../\'.$v[0]);\r\n				$dst = ImageCreateTrueColor($th_w, $th_h);\r\n				imagesavealpha($dst, true);\r\n				$cc=imagecolorallocatealpha($dst, 255, 255, 255, 127);\r\n				imagefill($dst, 0, 0, $cc); \r\n				ImageCopyResampled($dst, $src, 0, 0, $posX, $posY, $th_w, $th_h, $width, $height);\r\n				ImagePng($dst,$new_path);\r\n				break;\r\n			}\r\n			imagedestroy($src);\r\n			imagedestroy($dst);\r\n			if (empty($v[1]) && $auto_big) $v[1]=$v[0];\r\n			$v[0]=$dirname.$prefix.$filename;\r\n			ob_end_clean();\r\n		}\r\n	}\r\n	$tmplvars[$tvid][1]=str_replace(\'\\\\/\', \'/\', json_encode($fotoArr));\r\n}\r\n}\r\n//?>','0','&tvIds=TV Ids;text;&templ=Template;text;&role=Role;text;&resize=Resize: enable;list;true,false;false;&crop=Resize: cropping;list;true,false;true;&prefix=Resize: prefix;text;s_;&th_width=Resize: width;text;&th_height=Resize: height;text;&auto_big=Resize: auto big img;list;true,false;false;&auto_small=Resize: auto small img;list;true,false;false;&w=Preview: width;text;&h=Preview: height;text;&thumbUrl=PHPThumb URL;text;','1','');
INSERT INTO `skveez_site_plugins` VALUES ('8','customSettings','<strong>1.0</strong> Plugin to add your system settings','0','0','0','$e = &$modx->Event;\r\n$output = \"\";\r\n\r\nif ($e->name == \'OnSiteSettingsRender\'){\r\n\r\n$settingsArr = !empty($settings) ? explode(\'||\',$settings) : array(\'Example custom setting~custom_st_example\');\r\n\r\n$output .= \'<table>\';\r\n\r\nforeach($settingsArr as $key => $st_row){\r\n    $st_label_arr = explode(\'~\',$st_row);\r\n    $custom_st_label = trim($st_label_arr[0]);\r\n    $custom_st_name = isset($st_label_arr[1]) ? $st_label_arr[1] : \'custom_st\';\r\n    $custom_st_value = isset($st_label_arr[1]) && isset($modx->config[$st_label_arr[1]]) ? trim($modx->config[$st_label_arr[1]]) : \'\';\r\n    $output .= \'\r\n      <tr>\r\n        <td nowrap=\"nowrap\" class=\"warning\" width=\"200\">\'.$custom_st_label.\'</td>\r\n        <td><input type=\"text\" value=\"\'.$custom_st_value.\'\" name=\"\'.$custom_st_name.\'\" style=\"width: 350px;\" onchange=\"documentDirty=true;\" /></td>\r\n      </tr>\r\n    \';\r\n}\r\n\r\n$output .= \'</table>\';\r\n\r\n}\r\n\r\n$e->output($output);','0','&settings=Settings;textarea;Example custom setting~custom_st_example','1','');
INSERT INTO `skveez_site_plugins` VALUES ('9','CodeMirror','<strong>2.23 c</strong> JavaScript library that can be used to create a relatively pleasant editor interface','0','5','0','/**\r\n * @name: CodeMirror\r\n * @description: <b>2.23 b</b> JavaScript library that can be used to create a relatively pleasant editor interface\r\n *\r\n * @events:\r\n * - OnDocFormRender\r\n * - OnChunkFormRender\r\n * - OnModFormRender\r\n * - OnPluginFormRender\r\n * - OnSnipFormRender\r\n * - OnTempFormRender\r\n *\r\n */\r\n\r\n$_CM_BASE = \'assets/plugins/codemirror/\';\r\n\r\n$_CM_URL = $modx->config[\'site_url\'] . $_CM_BASE;\r\n\r\nrequire(MODX_BASE_PATH. $_CM_BASE .\'codemirror.plugin.php\');\r\n\r\n','0','&theme=Theme;list;,cobalt,ecllipse,elegant,monokai,neat,night,rubyblue; &indentUnit=Indent unit;int;2 &tabSize=The width of a tab character;int;2 &lineWrapping=lineWrapping;list;true,false;true','0','');
INSERT INTO `skveez_site_plugins` VALUES ('10','directResize','<strong>0.8.2</strong> Direct Resize','0','0','0','define(\'DIRECTRESIZE_PATH\', \"assets/plugins/directresize/\");\r\ndefine(\'DIRECTRESIZE_GALLERYDIR\', \"assets/drgalleries/\");\r\ninclude_once $modx->config[\'base_path\'].DIRECTRESIZE_PATH.\"directResize.php\";\r\n\r\nglobal $content;\r\n		\r\n$e = &$modx->Event;\r\nswitch ($e->name) {\r\n  case \"OnBeforeDocFormSave\":\r\n  		$content = ConvertFromBackend($_POST[\'ta\']);\r\n  		//if (isset($_POST[which_editor]) && $_POST[which_editor]!=\"none\" && strlen($_POST[\'ta\'])>0) $content[\'content\']  = ConvertFromBackend($_POST[\'ta\'], false);\r\n    break;\r\n    \r\n	case \"OnWebPagePrerender\":\r\n			//$modx->documentObject[content] = RenderOnFrontend($modx->documentObject[content], $config);\r\n			$modx->documentOutput = RenderOnFrontend($modx->documentOutput, $config);\r\n	break;\r\n		\r\n	case \"OnDocFormPrerender\":\r\n		// Плагин инициируется только в визуальном редакторе. Без редактора замены больших картинок на превью нет\r\n	         if (($modx->config[\'which_editor\'] != \"none\" && empty($_POST)) || (isset($_POST[\'which_editor\']) && $_POST[\'which_editor\']!=\"none\"))\r\n		{		\r\n			$content[\'content\'] = RenderOnFrontend($content[\'content\'], $config);\r\n		}\r\n		else\r\n		{\r\n			if (strlen($_POST[\'ta\'])>0)  $content[\'content\']  = ConvertFromBackend($_POST[\'ta\'], false);\r\n		}\r\n	break;\r\n\r\n	case \"OnCacheUpdate\":\r\n			ClearDRCache($clearCache);\r\n	break;	\r\n	\r\n	default :\r\n		return;\r\n	break;\r\n}','0','&config=Конфигурация;string;fancybox &clearCache=Очистка_кеша;list;0,1,2;2','1','');
INSERT INTO `skveez_site_plugins` VALUES ('11','Clear Comments','<strong>1.0</strong> Remove comments of documents when you empty MODx Trash Can.','0','0','0','/**\r\n * Clear Comments Plugin: Remove comments of documents when you empty MODx Trash Can.\r\n * Version: 1.0\r\n * Event: \"OnEmptyTrash\"\r\n * Requirement: Jot Snippet\r\n * Work on: Evolution\r\n *\r\n * Author: AHHP ~ Boplo.ir\r\n * Date: 21 August 2009\r\n*/\r\n\r\ndefined(\'IN_MANAGER_MODE\') or die();\r\nif($modx->Event->name == \"OnEmptyTrash\")\r\n{\r\n	$where = \'uparent IN(\' .join(\',\' , $ids). \')\';\r\n	$jot_content = $modx->getFullTableName(\'jot_content\');\r\n	$jot_fields = $modx->getFullTableName(\'jot_fields\');\r\n	$jot_subscriptions = $modx->getFullTableName(\'jot_subscriptions\');\r\n	\r\n	\r\n	// If `jot_fields` exists\r\n	if($modx->db->getRecordCount( $modx->db->query(\"SHOW TABLES LIKE \'$jot_fields\'\") ) == 1)\r\n	{\r\n		// `jot_fields` stores fields by comments ID so we need to get comments ID that are removing.\r\n		$commentsIds = array();\r\n		$select = $modx->db->select(\"id\", $jot_content, $where);\r\n		while($commentRow = $modx->db->getRow($select, \'num\'))\r\n			$commentsIds[] = $commentRow[0];\r\n		\r\n		if(count($commentsIds) > 0)\r\n			$modx->db->delete($jot_fields, \'id IN(\' .join(\",\",$commentsIds). \')\');\r\n		\r\n		unset($select, $commentsIds);\r\n	}\r\n	\r\n	$modx->db->delete($jot_content, $where);\r\n	$modx->db->delete($jot_subscriptions, $where);\r\n}','0','','1','');
INSERT INTO `skveez_site_plugins` VALUES ('12','Forgot Manager Login','<strong>1.1.2</strong> Resets your manager login when you forget your password via email confirmation','0','5','0','if(!class_exists(\'ForgotManagerPassword\')) {\r\n    class ForgotManagerPassword{\r\n        function ForgotManagerPassword(){\r\n            $this->errors = array();\r\n            $this->checkLang();\r\n        }\r\n\r\n        function getLink() {\r\n            global $_lang;\r\n  \r\n            $link = <<<EOD\r\n<a id=\"ForgotManagerPassword-show_form\" href=\"index.php?action=show_form\">{$_lang[\'forgot_your_password\']}</a>\r\nEOD;\r\n\r\n            return $link;\r\n        }\r\n\r\n        function getForm() {\r\n            global $_lang;\r\n\r\n            $form = <<<EOD\r\n<label id=\"FMP-email_label\" for=\"FMP_email\">{$_lang[\'account_email\']}:</label>\r\n<input id=\"FMP-email\" type=\"text\" />\r\n<button id=\"FMP-email_button\" type=\"button\" onclick=\"window.location = \'index.php?action=send_email&email=\'+document.getElementById(\'FMP-email\').value;\">{$_lang[\'send\']}</button>\r\nEOD;\r\n\r\n            return $form;\r\n        }\r\n\r\n        /* Get user info including a hash unique to this user, password, and day */\r\n        function getUser($user_id=false, $username=\'\', $email=\'\', $hash=\'\') {\r\n            global $modx, $_lang;\r\n\r\n            $user_id = $user_id == false ? false : $modx->db->escape($user_id);\r\n            $username = $modx->db->escape($username);\r\n            $email = $modx->db->escape($email);\r\n            $emaail = $modx->db->escape($hash);\r\n\r\n			$pre = $modx->db->config[\'table_prefix\'];\r\n			$site_id = $modx->config[\'site_id\'];\r\n			$today = date(\'Yz\'); // Year and day of the year\r\n			$wheres = array();\r\n			$where = \'\';\r\n			$user = null;\r\n  \r\n            if($user_id !== false) { $wheres[] = \"usr.id = \'{$user_id}\'\"; }\r\n            if(!empty($username)) { $wheres[] = \"usr.username = \'{$username}\'\"; }\r\n            if(!empty($email)) { $wheres[] = \"attr.email = \'{$email}\'\"; }\r\n            if(!empty($hash)) { $wheres[] = \"MD5(CONCAT(usr.username,usr.password,\'{$site_id}\',\'{$today}\')) = \'{$hash}\'\"; } \r\n\r\n            if($wheres) {\r\n                $where = \' WHERE \'.implode(\' AND \',$wheres);\r\n                $sql = \"SELECT usr.id, usr.username, attr.email, MD5(CONCAT(usr.username,usr.password,\'{$site_id}\',\'{$today}\')) AS hash\r\n                    FROM `{$pre}manager_users` usr\r\n                    INNER JOIN `{$pre}user_attributes` attr ON usr.id = attr.internalKey\r\n                    {$where}      \r\n                    LIMIT 1;\"; \r\n\r\n                if($result = $modx->db->query($sql)){\r\n                    if($modx->db->getRecordCount($result)==1) {\r\n                        $user = $modx->db->getRow($result);\r\n                    }\r\n                }\r\n            }\r\n\r\n            if($user == null) { $this->errors[] = $_lang[\'could_not_find_user\']; }\r\n\r\n            return $user;\r\n        }\r\n\r\n\r\n\r\n        /* Send an email with a link to login */\r\n        function sendEmail($to) {\r\n            global $modx, $_lang;\r\n\r\n            $subject = $_lang[\'password_change_request\'];\r\n            $headers  = \"MIME-Version: 1.0\\r\\n\".\r\n                \"Content-type: text/html; charset=\\\"{$modx->config[\'modx_charset\']}\\\"\\r\\n\".\r\n		\"From: MODx <{$modx->config[\'emailsender\']}>\\r\\n\".\r\n                \"Reply-To: no-reply@{$_SERVER[\'HTTP_HOST\']}\\r\\n\".\r\n                \"X-Mailer: PHP/\".phpversion();\r\n\r\n            $user = $this->getUser(0, \'\', $to);\r\n  \r\n            if($user[\'username\']) {\r\n                $body = <<<EOD\r\n<p>{$_lang[\'forgot_password_email_intro\']} <a href=\"{$modx->config[\'site_url\']}manager/processors/login.processor.php?username={$user[\'username\']}&hash={$user[\'hash\']}\">{$_lang[\'forgot_password_email_link\']}</a></p>\r\n<p>{$_lang[\'forgot_password_email_instructions\']}</p>\r\n<p><small>{$_lang[\'forgot_password_email_fine_print\']}</small></p>\r\nEOD;\r\n\r\n                $mail = mail($to, $subject, $body, $headers);\r\n                if(!$mail) { $this->errors[] = $_lang[\'error_sending_email\']; }\r\n   \r\n                return $mail;  \r\n            }\r\n        }\r\n\r\n        function unblockUser($user_id) {\r\n            global $modx, $_lang;\r\n\r\n            $pre = $modx->db->config[\'table_prefix\'];\r\n            $modx->db->update(array(\'blocked\' => 0, \'blockeduntil\' => 0, \'failedlogincount\' => 0), \"`{$pre}user_attributes`\", \"internalKey = \'{$user_id}\'\");\r\n\r\n            if(!$modx->db->getAffectedRows()) { $this->errors[] = $_lang[\'user_doesnt_exist\']; return; }\r\n\r\n            return true;\r\n        }\r\n\r\n        function checkLang() {\r\n            global $_lang;\r\n\r\n            $eng = array();\r\n            $eng[\'forgot_your_password\'] = \'Forgot your password?\';\r\n            $eng[\'account_email\'] = \'Account email\';\r\n            $eng[\'send\'] = \'Send\';\r\n            $eng[\'password_change_request\'] = \'Password change request\';\r\n            $eng[\'forgot_password_email_intro\'] = \'A request has been made to change the password on your account.\';\r\n            $eng[\'forgot_password_email_link\'] = \'Click here to complete the process.\';\r\n            $eng[\'forgot_password_email_instructions\'] = \'From there you will be able to change your password from the My Account menu.\';\r\n            $eng[\'forgot_password_email_fine_print\'] = \'* The URL above will expire once you change your password or after today.\';\r\n            $eng[\'error_sending_email\'] = \'Error sending email\';\r\n            $eng[\'could_not_find_user\'] = \'Could not find user\';\r\n            $eng[\'user_doesnt_exist\'] = \'User does not exist\';\r\n            $eng[\'email_sent\'] = \'Email sent\';\r\n\r\n            foreach($eng as $key=>$value) {\r\n                if(empty($_lang[$key])) { $_lang[$key] = $value; }\r\n            }  \r\n        }\r\n\r\n        function getErrorOutput() {\r\n            $output = \'\';\r\n\r\n            if($this->errors) {\r\n                $output = \'<span class=\"error\">\'.implode(\'</span><span class=\"errors\">\', $this->errors).\'</span>\';\r\n            }\r\n  \r\n            return $output;\r\n        }\r\n    }\r\n}\r\n\r\nglobal $_lang;\r\n\r\n$output = \'\';\r\n$event_name = $modx->Event->name;\r\n$action = (empty($_GET[\'action\']) ? \'\' : $_GET[\'action\']);\r\n$username = (empty($_GET[\'username\']) ? false : $_GET[\'username\']);\r\n$to = (empty($_GET[\'email\']) ? \'\' : $_GET[\'email\']);\r\n$hash = (empty($_GET[\'hash\']) ? false : $_GET[\'hash\']);\r\n$forgot = new ForgotManagerPassword();\r\n\r\nif($event_name == \'OnManagerLoginFormRender\') {\r\n    switch($action) {\r\n        case \'show_form\':\r\n            $output = $forgot->getForm();\r\n            break;\r\n        case \'send_email\':\r\n            if($forgot->sendEmail($to)) { $output = $_lang[\'email_sent\']; }\r\n            break;\r\n        default:\r\n            $output = $forgot->getLink();\r\n            break;\r\n    }\r\n\r\n    if($forgot->errors) { $output = $forgot->getErrorOutput() . $forgot->getLink(); }\r\n}\r\n\r\nif($event_name == \'OnBeforeManagerLogin\') {\r\n    $user = $forgot->getUser(false, $username, \'\', $hash);\r\n    if($user && is_array($user) && !$forgot->errors) {\r\n        $forgot->unblockUser($user[\'id\']);\r\n    }\r\n}\r\n\r\nif($event_name == \'OnManagerAuthentication\' && $hash && $username) {\r\n    $user = $forgot->getUser(false, $username, \'\', $hash);\r\n    $output = ($user !== null && count($forgot->errors) == 0) ? true : false;\r\n}\r\n\r\n$modx->Event->output($output);','0','','0','');
INSERT INTO `skveez_site_plugins` VALUES ('13','custom Template','<strong>0.1</strong> Newly created Resources use the template configured in plugin','0','0','0','/*\r\n * custom Template\r\n *\r\n * Written By Dmi3yy - 13 Sep 2011\r\n *\r\n *\r\n * Configuration:\r\n * check the OnDocFormPrerender event\r\n * add properties &customTemplates=customTemplates;text;100,101|102,103\r\n * Version 0.1\r\n *\r\n */\r\n\r\nglobal $content;\r\n$e = &$modx->Event;\r\n$cusTemp=explode(\'|\',$customTemplates);\r\nswitch($e->name) {\r\n    case \'OnDocFormPrerender\':        \r\n      \r\n        \r\n        if ($parent = $modx->getPageInfo($_REQUEST[\'pid\'],0,\'template\')) {\r\n\r\n              foreach ($cusTemp as $value) {\r\n                  $tpl=explode(\',\',$value);\r\n                  if ($parent[\'template\'] == $tpl[0]){ $content[\'template\'] = $tpl[1];} \r\n              }   \r\n                 \r\n        }\r\n     \r\n        break;\r\n    default:\r\n        break;\r\n}','0','&customTemplates=customTemplates;text;100,101|102,103','1','');
INSERT INTO `skveez_site_plugins` VALUES ('14','MultiFiles','<strong>1.0.2</strong> Добавление нескольких файлов к странице','0','0','0','//defined(\'IN_MANAGER_MODE\') or die();\r\n\r\nglobal $content,$default_template;\r\n$tvIds = isset($tvIds) ? $tvIds : 101;\r\n$templ = isset($templ) ? explode(\',\',$templ) : false;\r\n$role = isset($role) ? explode(\',\',$role) : false;\r\n$cur_templ = isset($_POST[\'template\']) ? $_POST[\'template\'] : (isset($content[\'template\']) ? $content[\'template\'] : $default_template);\r\n$cur_role = $_SESSION[\'mgrRole\'];\r\nif (($templ && !in_array($cur_templ,$templ)) || ($role && !in_array($cur_role,$role))) return;\r\n\r\n$lang[\'insert\']=\'Вставить\';\r\n$lang[\'url\']=\'Адрес:\';\r\n$lang[\'title\']=\'Название:\';\r\n\r\n$e = &$modx->Event;\r\nif ($e->name == \'OnDocFormRender\') {\r\nrequire_once(MODX_MANAGER_PATH.\'includes/tmplvars.inc.php\');\r\n$modx_script = renderFormElement(\'file\',0,\'\',\'\',\'\');\r\npreg_match(\'/(<script[^>]*?>.*?<\\/script>)/si\', $modx_script, $matches);\r\n$output = $matches[0];\r\n$output .= <<< OUT\r\n<!-- MultiFiles -->\r\n<style type=\"text/css\">\r\n.fileitem {border:1px solid #e3e3e3; margin:0 0 5px; padding:2px 5px 5px 5px; overflow:hidden; white-space:nowrap; zoom:1}\r\n.fileitem span {display:inline-block; padding-top:3px;}\r\n.fileitem input {line-height:1.1; vertical-align:middle;}\r\n</style>\r\n<script type=\"text/javascript\">\r\nwindow.ie9=window.XDomainRequest && window.performance; window.ie=window.ie && !window.ie9; /* IE9 patch */\r\nvar MultiFiles = new Class({\r\n	initialize: function(fid){\r\n		this.name = fid;\r\n		this.fid = $(fid);\r\n		var fArr = (this.fid.value && this.fid.value!=\'[]\') ? Json.evaluate(this.fid.value) : [null];\r\n		this.fid.setStyle(\'display\',\'none\');\r\n		this.box = new Element(\'div\',{\'class\':\'fileEditor\'});\r\n		this.fid.getParent().adopt(this.box);\r\n		this.file=0;\r\n		for (var f=0;f<fArr.length;f++) this.addItem(fArr[f]);\r\n		if (typeof(SetUrl) != \'undefined\') {\r\n			this.OrigSetUrl = SetUrl;\r\n			SetUrl = function(url, width, height, alt) {\r\n				var lastFile = lastFileCtrl;\r\n				this.OrigSetUrl(url, width, height, alt);\r\n				if ($(lastFile)!=null) $(lastFile).fireEvent(\'change\');\r\n			}.bind(this)\r\n		}\r\n		this.sort=new Sortables(this.box,{\r\n			onStart: function(el){el.setStyles({\'background\':\'#f0f0f0\',\'opacity\':1});},\r\n			onComplete: function(el){el.setStyle(\'background\',\'none\');this.setEditor();}.bind(this)\r\n		});	\r\n		this.box.getElements(\'div.fileitem\').setStyle(\'cursor\',\'move\');\r\n		this.box.getElements(\'input[type=text]\').addEvent(\'click\',function(){this.focus();});\r\n	},\r\n	br: function(){return new Element(\'br\');},\r\n	sp: function(text){return new Element(\'span\').setText(text);},\r\n	addItem: function(values,elem){\r\n		this.file++;\r\n		var f = this.file;\r\n		var rowDiv = new Element(\'div\',{\'class\':\'fileitem\'});\r\n		if (elem) {rowDiv.injectAfter(elem);} else {this.box.adopt(rowDiv);}\r\n		if (!values) values=[\'\',\'\']; \r\n		var fileURL = new Element(\'input\',{\'type\':\'text\',\'name\':\'file_\'+this.name+\'_\'+f,\'id\':\'file_\'+this.name+\'_\'+f,\'class\':\'fileField\',\'value\':values[0],\'events\':{\r\n			\'change\':function(){this.setEditor();}.bind(this)\r\n		}});\r\n		var bInsert = new Element(\'input\',{\'type\':\'button\',\'value\':\'{$lang[\'insert\']}\',\'events\':{\r\n			\'click\':function(){BrowseFileServer(\'file_\'+this.name+\'_\'+f)}.bind(this)\r\n		}});\r\n		var fileName = new Element(\'input\',{\'type\':\'text\',\'class\':\'fileField\',\'value\':values[1],\'events\':{\r\n			\'keyup\':function(){this.setEditor();documentDirty=true;}.bind(this)\r\n		}});\r\n		var bAdd = new Element(\'input\',{\'type\':\'button\',\'value\':\'+\',\'events\': {\r\n			\'click\':function(){this.addItem(null,rowDiv);}.bind(this)\r\n		}});\r\n		rowDiv.adopt(this.sp(\'{$lang[\'url\']}\'),this.br(),fileURL,bInsert,this.br(),this.sp(\'{$lang[\'title\']}\'),this.br(),fileName,bAdd);\r\n		if (this.box.getElements(\'div.fileitem\').length>1) rowDiv.adopt(new Element(\'input\',{\'type\':\'button\',\'value\':\'-\',\'events\':{\r\n			\'click\':function(){rowDiv.remove();this.setEditor();}.bind(this)\r\n		}}));\r\n		fileURL.fireEvent(\'change\');\r\n	},\r\n	setEditor: function(){\r\n		var fArr=new Array();\r\n		this.box.getElements(\'div.fileitem\').each(function(item){\r\n			var itemsArr=new Array();\r\n			var inputs=item.getElements(\'input[type=text]\');\r\n			var noempty=false;\r\n			inputs.each(function(item){itemsArr.push(item.value); if (item.value) noempty=true;});\r\n			if (noempty) fArr.push(itemsArr);\r\n		});\r\n		this.fid.value = Json.toString(fArr);\r\n	}\r\n});\r\nwindow.addEvent(\'domready\', function(){\r\n	var tvIds = [$tvIds];\r\n	for (var i=0;i<tvIds.length;i++){\r\n		var fid = \'tv\'+ tvIds[i];\r\n		if($(fid)!=null) {var modxMultiFiles=new MultiFiles(fid);}\r\n	}\r\n});\r\n</script>\r\n<!-- /MultiFiles -->\r\nOUT;\r\n$e->output($output);\r\n}\r\n//?>','0','&tvIds=TV Ids;text;&templ=Template;text;&role=Role;text;','1','');
INSERT INTO `skveez_site_plugins` VALUES ('15','TinyMCE Rich Text Editor','<strong>3.3.9.2</strong> Javascript WYSIWYG Editor','0','5','0','// Set the name of the plugin folder\r\n$plugin_dir = \"tinymce\";\r\n\r\n// Set path and base setting variables\r\nif(!isset($mce_path))\r\n{ \r\n	$mce_path = MODX_BASE_PATH . \'assets/plugins/\'.$plugin_dir . \'/\'; \r\n	$mce_url  = MODX_BASE_URL  . \'assets/plugins/\'.$plugin_dir . \'/\'; \r\n}\r\n\r\n$params[\'customparams\']    = $customparams;\r\n$params[\'blockFormats\']    = $mce_formats;\r\n$params[\'entity_encoding\'] = $entity_encoding;\r\n$params[\'entities\']        = $entities;\r\n$params[\'pathoptions\']     = $mce_path_options;\r\n$params[\'resizing\']        = $mce_resizing;\r\n$params[\'disabledButtons\'] = $disabledButtons;\r\n$params[\'link_list\']       = $link_list;\r\n$params[\'theme\']           = $webtheme;\r\n$params[\'custom_plugins\']  = $webPlugins;\r\n$params[\'custom_buttons1\'] = $webButtons1;\r\n$params[\'custom_buttons2\'] = $webButtons2;\r\n$params[\'custom_buttons3\'] = $webButtons3;\r\n$params[\'custom_buttons4\'] = $webButtons4;\r\n$params[\'toolbar_align\']   = $webAlign;\r\n$params[\'width\']           = $width;\r\n$params[\'height\']          = $height;\r\n\r\n$params[\'mce_path\']        = $mce_path;\r\n$params[\'mce_url\']         = $mce_url;\r\n\r\ninclude_once $mce_path . \'lang/tinymce.lang.php\';\r\ninclude_once $mce_path . \'tinymce.functions.php\';\r\n\r\n$mce = new TinyMCE($params);\r\n\r\n// Handle event\r\n$e = &$modx->Event; \r\nswitch ($e->name)\r\n{\r\n	case \"OnRichTextEditorRegister\": // register only for backend\r\n		$e->output(\"TinyMCE\");\r\n		break;\r\n\r\n	case \"OnRichTextEditorInit\": \r\n		if($editor!==\"TinyMCE\") return;\r\n		\r\n		$params[\'elements\']        = $elements;\r\n		$params[\'css_selectors\']   = $modx->config[\'tinymce_css_selectors\'];\r\n		$params[\'use_browser\']     = $modx->config[\'use_browser\'];\r\n		$params[\'editor_css_path\'] = $modx->config[\'editor_css_path\'];\r\n		\r\n		if($modx->isBackend() || (intval($_GET[\'quickmanagertv\']) == 1 && isset($_SESSION[\'mgrValidated\'])))\r\n		{\r\n			$params[\'theme\']           = $modx->config[\'tinymce_editor_theme\'];\r\n			$params[\'language\']        = getTinyMCELang($modx->config[\'manager_language\']);\r\n			$params[\'frontend\']        = false;\r\n			$params[\'custom_plugins\']  = $modx->config[\'tinymce_custom_plugins\'];\r\n			$params[\'custom_buttons1\'] = $modx->config[\'tinymce_custom_buttons1\'];\r\n			$params[\'custom_buttons2\'] = $modx->config[\'tinymce_custom_buttons2\'];\r\n			$params[\'custom_buttons3\'] = $modx->config[\'tinymce_custom_buttons3\'];\r\n			$params[\'custom_buttons4\'] = $modx->config[\'tinymce_custom_buttons4\'];\r\n			$params[\'toolbar_align\']   = $modx->config[\'manager_direction\'];\r\n			$params[\'webuser\']         = null;\r\n			\r\n			$html = $mce->get_mce_script($params);\r\n		}\r\n		else\r\n		{\r\n			$frontend_language = isset($modx->config[\'fe_editor_lang\']) ? $modx->config[\'fe_editor_lang\']:\'\';\r\n			$webuser = (isset($modx->config[\'rb_webuser\']) ? $modx->config[\'rb_webuser\'] : null);\r\n			\r\n			$params[\'webuser\']         = $webuser;\r\n			$params[\'language\']        = getTinyMCELang($frontend_language);\r\n			$params[\'frontend\']        = true;\r\n			\r\n			$html = $mce->get_mce_script($params);\r\n		}\r\n		$e->output($html);\r\n		break;\r\n\r\n	case \"OnInterfaceSettingsRender\":\r\n		global $usersettings,$settings;\r\n		$action = $modx->manager->action;\r\n		switch ($action)\r\n		{\r\n			case 11:\r\n				$mce_settings = \'\';\r\n				break;\r\n			case 12:\r\n				$mce_settings = $usersettings;\r\n				break;\r\n			case 17:\r\n				$mce_settings = $settings;\r\n				break;\r\n			default:\r\n				$mce_settings = $settings;\r\n				break;\r\n		}\r\n		\r\n		$params[\'use_editor\']       = $modx->config[\'base_url\'].$modx->config[\'use_editor\'];\r\n        $params[\'editor_css_path\']  = $modx->config[\'editor_css_path\'];\r\n		$params[\'theme\']            = $mce_settings[\'tinymce_editor_theme\'];\r\n		$params[\'css_selectors\']    = $mce_settings[\'tinymce_css_selectors\'];\r\n		$params[\'custom_plugins\']   = $mce_settings[\'tinymce_custom_plugins\'];\r\n		$params[\'custom_buttons1\']  = $mce_settings[\'tinymce_custom_buttons1\'];\r\n		$params[\'custom_buttons2\']  = $mce_settings[\'tinymce_custom_buttons2\'];\r\n		$params[\'custom_buttons3\']  = $mce_settings[\'tinymce_custom_buttons3\'];\r\n		$params[\'custom_buttons4\']  = $mce_settings[\'tinymce_custom_buttons4\'];\r\n		\r\n		$html = $mce->get_mce_settings($params);\r\n		$e->output($html);\r\n		break;\r\n		\r\n	default :\r\n		return; // stop here - this is very important. \r\n		break; \r\n}\r\n','0','&customparams=Custom Parameters;textarea; &mce_formats=Block Formats;text;p,h1,h2,h3,h4,h5,h6,div,blockquote,code,pre &entity_encoding=Entity Encoding;list;named,numeric,raw;named &entities=Entities;text; &mce_path_options=Path Options;list;rootrelative,docrelative,fullpathurl;docrelative &mce_resizing=Advanced Resizing;list;true,false;true &disabledButtons=Disabled Buttons;text; &link_list=Link List;list;enabled,disabled;enabled &webtheme=Web Theme;list;simple,editor,creative,custom;simple &webPlugins=Web Plugins;text;style,advimage,advlink,searchreplace,contextmenu,paste,fullscreen,nonbreaking,xhtmlxtras,visualchars,media &webButtons1=Web Buttons 1;text;undo,redo,selectall,|,pastetext,pasteword,|,search,replace,|,nonbreaking,hr,charmap,|,image,link,unlink,anchor,media,|,cleanup,removeformat,|,fullscreen,code,help &webButtons2=Web Buttons 2;text;bold,italic,underline,strikethrough,sub,sup,|,|,blockquote,bullist,numlist,outdent,indent,|,justifyleft,justifycenter,justifyright,justifyfull,|,styleselect,formatselect,|,styleprops &webButtons3=Web Buttons 3;text; &webButtons4=Web Buttons 4;text; &webAlign=Web Toolbar Alignment;list;ltr,rtl;ltr &width=Width;text;100% &height=Height;text;400','0','');
INSERT INTO `skveez_site_plugins` VALUES ('16','ajaxSubmit','<strong>1.0</strong> Ajax sending of any form','0','0','0','if(!isset($post_signal)) $post_signal = \'ajax_submit\';\r\nif(!isset($check_referer)) $check_referer = \'yes\';\r\n\r\n$e = $modx->Event;\r\n\r\nif ($e->name == \'OnLoadWebDocument\' || $e->name == \'OnLoadWebPageCache\'){\r\n  \r\n  $output = \'\';\r\n  \r\n  $referer_valid = $check_referer==\'yes\' ? in_array(strpos($_SERVER[\'HTTP_REFERER\'],$_SERVER[\'HTTP_HOST\']),array(7,8)) : true;\r\n  \r\n  if(isset($_SERVER[\'HTTP_X_REQUESTED_WITH\']) && strtolower($_SERVER[\'HTTP_X_REQUESTED_WITH\']) == \'xmlhttprequest\' && $referer_valid){\r\n    \r\n    if(isset($_POST[$post_signal])){\r\n        \r\n        class exDocumentParser extends DocumentParser {\r\n          function sendRedirect() {\r\n            return true;\r\n          }\r\n          function innerHTML($node){\r\n            $doc = new DOMDocument();\r\n            foreach ($node->childNodes as $child)\r\n                $doc->appendChild($doc->importNode($child, true));\r\n            return $doc->saveHTML();\r\n          }\r\n        }\r\n        \r\n        $xpath = $_POST[$post_signal];\r\n        \r\n        define(AS_PATH, MODX_BASE_PATH.\'assets/plugins/ajax_submit/\');\r\n        define(AS_URL_PATH, MODX_BASE_URL.\'assets/plugins/ajax_submit/\');\r\n        \r\n        $parser = new exDocumentParser;\r\n        $parser->db->connect();\r\n        $parser->getSettings();\r\n        $parser->config = $modx->config;\r\n        $parser->documentObject = $modx->documentObject;\r\n        $parser->documentIdentifier = $modx->documentIdentifier;\r\n        $parser->aliasListing = $modx->aliasListing;\r\n        $parser->snippetCache = $modx->snippetCache;\r\n        $parser->chunkCache = $modx->chunkCache;\r\n        $parser->documentListing = $modx->documentListing;\r\n        $parser->documentMap = $modx->documentMap;\r\n        \r\n        $html = $modx->documentContent;\r\n        $html = $parser->mergeChunkContent($html);\r\n        $html = $parser->mergeDocumentContent($html);\r\n        $html = $parser->mergeSettingsContent($html);\r\n        $html = $parser->parseDocumentSource($html);\r\n        if(strpos($html, \'[!\') > -1){\r\n            $html = str_replace(array(\'[!\',\'!]\'),array(\'[[\',\']]\'),$html);\r\n            $html = $parser->parseDocumentSource($html);\r\n        }\r\n        $html = $parser->rewriteUrls($html);\r\n        \r\n        if($modx->config[\'modx_charset\']==\"UTF-8\"){\r\n            $html = mb_convert_encoding($html, \'HTML-ENTITIES\', \'utf-8\');\r\n        }\r\n        \r\n        //search snippet content in html\r\n        require_once AS_PATH.\'Dom/Query.php\';\r\n        $dom = new Zend_Dom_Query($html);\r\n        $results = $dom->query($xpath);\r\n        if (count($results) > 0){\r\n            $output = $parser->innerHTML($results->current());\r\n            $output = trim($output);\r\n        }\r\n        \r\n        if(!$output) $output = \'success\';\r\n        \r\n        echo $output;\r\n        exit;\r\n        \r\n    }\r\n    \r\n  }\r\n  \r\n}\r\n','0','&post_signal=Post signal name;string;ajax_submit &check_referer=Check referer;list;yes,no;yes','1','');
INSERT INTO `skveez_site_plugins` VALUES ('17','systemField','<strong>1.3</strong> The plugin allows you to add \"system\" data in a separate field, which will be displayed along with the page content.','0','0','0','/*\r\nsystemField 1.3 plugin for MODx Evo\r\nThe plugin allows you to add \"system\" data in a separate field, which will be displayed along with the page content.\r\nby Andhir\r\nСделано для студии \"Симпл дрим\" - http://www.simpledream.ru/\r\n-----------------------------------------\r\nSystem event: OnDocFormRender\r\nConfigurtion:\r\n&separator=Separator;string;<!-- hr --> &open_text=Open link text;string;system content &position=System content position;list;before,after;after\r\n*/\r\n\r\ndefined(\'IN_MANAGER_MODE\') or die();\r\n\r\nif(empty($separator)) $separator = \'<!-- hr -->\';\r\nif(empty($open_text)) $open_text = \'system content\';\r\nif(empty($position)) $position = \'after\';\r\n\r\n$get_action = isset($_GET[\'a\']) ? $_GET[\'a\'] : 27;\r\n\r\n$e = &$modx->Event;\r\n\r\nif ($e->name == \'OnDocFormRender\') {\r\n\r\n$output = <<< OUT\r\n\r\n<!-- systemField -->\r\n<script type=\"text/javascript\">\r\n\r\nvar SFloadAction = navigator.userAgent.indexOf(\'AppleWebKit\')>-1 ? \'load\' : \'domready\';\r\nvar SFposition = \'{$position}\';\r\n\r\n//window.addEvent(SFloadAction, function(){\r\n  \r\n  var SFseparator = \'{$separator}\';\r\n  var SFsourceContentField = $(\'ta\');\r\n  var SFsaveButton = $(\'Button1\').getFirst(\'a\');\r\n  var SFcontent = SFsourceContentField.value;\r\n  var SFsepPos = SFcontent.indexOf(SFseparator);\r\n  \r\n  function SFonSaveContent(){\r\n    /*\r\n    if(typeof(tinyMCE)!=\'undefined\'){\r\n      var SFoutContent = tinyMCE.get(\'ta\').getContent()+SFseparator+SFtextarea.value;\r\n      tinyMCE.get(\'ta\').setContent(SFoutContent,{format : \'html\'});\r\n    }else{\r\n    */\r\n      if(SFtextarea.value.length>0){\r\n        if(SFposition==\'after\')\r\n          SFsourceContentField.value += SFseparator+SFtextarea.value;\r\n        else\r\n          SFsourceContentField.value = $(\'SFcontent\').value+SFseparator+SFsourceContentField.value;\r\n      }\r\n    //}\r\n  }\r\n  \r\n  if(SFsepPos>-1){\r\n    var SFsystemValue = SFposition==\'after\' ? SFcontent.substr(SFsepPos+SFseparator.length) : SFcontent.substr(0,SFsepPos);\r\n    SFsourceContentField.value = SFposition==\'after\' ? SFcontent.substr(0,SFsepPos) : SFcontent.substr(SFsepPos+SFseparator.length);\r\n  }else{\r\n    var SFsystemValue = \'\';\r\n  }\r\n  \r\n  var SFlinkOpen = new Element(\'a\',{\r\n    href: \'#open\',\r\n    events: {\r\n      click: function(){\r\n        if(SFtextarea.style.display==\'none\')\r\n          SFtextarea.style.display=\'inline\';\r\n        else\r\n          SFtextarea.style.display=\'none\';\r\n        return false;\r\n      }\r\n    },\r\n    styles: {display:\'block\', margin: \'10px 0\'}\r\n  });\r\n  var br = new Element(\'br\');\r\n  var SFtextarea = new Element(\'textarea\',{\r\n    cols: 300,\r\n    rows: 12,\r\n    id: \'SFcontent\',\r\n    styles: {width: \'400px\', height: \'200px\', display: \'none\'},\r\n    value: SFsystemValue\r\n  });\r\n  \r\n  $(\'ta\').getParent().adopt(SFlinkOpen.appendText(\'{$open_text}\'),SFtextarea);\r\n  \r\n  if($(\'which_editor\')!=null){\r\n    $(\'which_editor\')\r\n    .removeEvents(\'change\')\r\n    .removeProperty(\'onchange\')\r\n    .addEvent(\'change\',function(){\r\n        documentDirty=false;\r\n      	document.mutate.a.value = {$get_action};\r\n        document.mutate.newtemplate.value = $(\'template\').value;\r\n      	document.mutate.which_editor.value = this.value;\r\n        document.mutate.mode.value = 4; \r\n        SFsubmit();\r\n    });\r\n  }\r\n  \r\n  $(\'template\')\r\n  .removeEvents(\'change\')\r\n  .removeProperty(\'onchange\')\r\n  .addEvent(\'change\',function(){\r\n    documentDirty=false;\r\n		document.mutate.a.value = {$get_action};\r\n		document.mutate.newtemplate.value = this.value;\r\n		SFsubmit();\r\n  });\r\n  \r\n//});\r\nwindow.addEvent(\'domready\', function(){\r\n  if(navigator.userAgent.indexOf(\'MSIE\')>-1 && navigator.userAgent.indexOf(\'MSIE 9\')==-1) {\r\n    $(\'mutate\').addEvent(\'submit\',SFonSaveContent);\r\n    SFsubmit = function(){document.mutate.save.click();}\r\n  }else{\r\n    SFsubmit = function(){\r\n      $(\'mutate\').addEvent(\'submit\',SFonSaveContent);\r\n      document.mutate.save.click();\r\n    }\r\n  }\r\n  \r\n  SFsaveButton\r\n  .removeEvents(\'click\')\r\n  .removeProperty(\'onclick\')\r\n  .addEvent(\'click\',function(){\r\n    documentDirty = false;\r\n    SFsubmit();\r\n    return false;\r\n  });\r\n});\r\n</script>\r\n<!-- /systemField -->\r\n\r\nOUT;\r\n\r\n$e->output($output);\r\n\r\n}\r\n','0','&separator=Separator;string;<!-- hr --> &open_text=Open link text;string;system content &position=System content position;list;before,after;after','1','');
INSERT INTO `skveez_site_plugins` VALUES ('18','Quick Manager+','<strong>1.5.5</strong> Enables QuickManager+ front end content editing support','0','0','0','// In manager\r\nif (isset($_SESSION[\'mgrValidated\'])) {\r\n\r\n    $show = TRUE;\r\n\r\n    if ($disabled  != \'\') {\r\n        $arr = explode(\",\", $disabled );\r\n        if (in_array($modx->documentIdentifier, $arr)) {\r\n            $show = FALSE;\r\n        }\r\n    }\r\n\r\n    if ($show) {\r\n        // Replace [*#tv*] with QM+ edit TV button placeholders\r\n        if ($tvbuttons == \'true\') {\r\n            $e = $modx->Event;\r\n            if ($e->name == \'OnParseDocument\') {\r\n                 $output = &$modx->documentOutput;\r\n                 $output = preg_replace(\'~\\[\\*#(.*?)\\*\\]~\', \'<!-- \'.$tvbclass.\' $1 -->[*$1*]\', $output);\r\n                 $modx->documentOutput = $output;\r\n             }\r\n         }\r\n        // In manager\r\n        if (isset($_SESSION[\'mgrValidated\'])) {\r\n            include_once($modx->config[\'base_path\'].\'assets/plugins/qm/qm.inc.php\');\r\n            $qm = new Qm($modx, $jqpath, $loadmanagerjq, $loadfrontendjq, $noconflictjq, $loadtb, $tbwidth, $tbheight, $hidefields, $hidetabs, $hidesections, $addbutton, $tpltype, $tplid, $custombutton, $managerbutton, $logout, $autohide, $editbuttons, $editbclass, $newbuttons, $newbclass, $tvbuttons, $tvbclass);\r\n        }\r\n    }\r\n}','0','&jqpath=Path to jQuery;text;assets/js/jquery-1.4.4.min.js &loadmanagerjq=Load jQuery in manager;list;true,false;false &loadfrontendjq=Load jQuery in front-end;list;true,false;true &noconflictjq=jQuery noConflict mode in front-end;list;true,false;true &loadtb=Load modal box in front-end;list;true,false;true &tbwidth=Modal box window width;text;80% &tbheight=Modal box window height;text;90% &hidefields=Hide document fields from front-end editors;text;parent &hidetabs=Hide document tabs from front-end editors;text; &hidesections=Hide document sections from front-end editors;text; &addbutton=Show add document here button;list;true,false;true &tpltype=New document template type;list;parent,id,selected;parent &tplid=New document template id;int;3 &custombutton=Custom buttons;textarea; &1=undefined;; &managerbutton=Show go to manager button;list;true,false;true &logout=Logout to;list;manager,front-end;manager &disabled=Plugin disabled on documents;text; &autohide=Autohide toolbar;list;true,false;true &editbuttons=Inline edit buttons;list;true,false;false &editbclass=Edit button CSS class;text;qm-edit &newbuttons=Inline new resource buttons;list;true,false;false &newbclass=New resource button CSS class;text;qm-new &tvbuttons=Inline template variable buttons;list;true,false;false &tvbclass=Template variable button CSS class;text;qm-tv','1','');
INSERT INTO `skveez_site_plugins` VALUES ('19','devStat','<strong>1.0</strong> Logging developer info','0','0','0','$e = &$modx->Event;\r\n\r\nif ($e->name == \'OnWebPagePrerender\'){\r\n  \r\n  if(!isset($alert_time)) $alert_tim = 3;\r\n  if(!isset($alert_query)) $alert_query = 100;\r\n  \r\n  $totalTime = ($modx->getMicroTime() - $modx->tstart);\r\n  $queryTime = $modx->queryTime;\r\n  $phpTime = $totalTime - $queryTime;\r\n\r\n  $queryTime = sprintf(\"%2.4f сек.\", $queryTime);\r\n  $totalTime = sprintf(\"%2.4f сек.\", $totalTime);\r\n  $phpTime = sprintf(\"%2.4f сек.\", $phpTime);\r\n  $source = $modx->documentGenerated == 1 ? \"базы данных\" : \"кэша\";\r\n  $queries = isset ($modx->executedQueries) ? $modx->executedQueries : 0;\r\n  \r\n  if($totalTime >= $alert_time || $queries >= $alert_query){\r\n    $page_url = \'http://\'.$_SERVER[\'HTTP_HOST\'].$_SERVER[\'REQUEST_URI\'];\r\n    $info_mess = \'<a href=\"\'.$page_url.\'\" target=\"_blank\">\'.$page_url.\'</a>: \';\r\n    $info_mess .= \"<b>MySQL</b>: $queryTime, $queries запрос(ов), <b>PHP</b>: $phpTime, <b>общее время</b>: $totalTime, документ получен из <b>$source</b>\";\r\n    $modx->logEvent(45,1,$info_mess,\"devStat: $queries / $totalTime\");\r\n  }\r\n\r\n}\r\n\r\n\r\n//?>','0','&alert_time=Критичное время генерации;int;3 &alert_query=Критичное число запросов;int;100','1','');
INSERT INTO `skveez_site_plugins` VALUES ('20','TvTable','<strong>1.11</strong> Добавление к странице таблицы данных','0','0','0','//defined(\'IN_MANAGER_MODE\') or die();\r\n\r\nglobal $content,$default_template,$tmplvars;\r\n$tvIds = isset($tvIds) ? $tvIds : 102;\r\n$templ = isset($templ) ? explode(\',\',$templ) : false;\r\n$role = isset($role) ? explode(\',\',$role) : false;\r\n$cur_templ = isset($_POST[\'template\']) ? $_POST[\'template\'] : (isset($content[\'template\']) ? $content[\'template\'] : $default_template);\r\n$cur_role = $_SESSION[\'mgrRole\'];\r\nif (($templ && !in_array($cur_templ,$templ)) || ($role && !in_array($cur_role,$role))) return;\r\n\r\n$loadfile = isset($loadfile)&&($loadfile==\'true\') ? \"this.box.adopt(new Element(\'input\',{\'type\':\'file\',\'name\':\'file_\'+fid,\'styles\':{\'margin-top\':\'5px\'}}));\" : \"\";\r\n\r\n$e = &$modx->Event;\r\nif ($e->name == \'OnDocFormRender\') {\r\n$output = <<< OUT\r\n<!-- TvTable -->\r\n<script type=\"text/javascript\">\r\nwindow.ie9=window.XDomainRequest && window.performance; window.ie=window.ie && !window.ie9; /* IE9 patch */\r\nvar TvTable = new Class({\r\n	initialize: function(fid){\r\n		this.fid = $(fid);\r\n		var tvtArr = (this.fid.value) ? Json.evaluate(this.fid.value) : [null,null];\r\n		this.fid.setStyle(\'display\',\'none\');\r\n		this.box = new Element(\'div\',{\'class\':\'tvtEditor\'});\r\n		this.fid.getParent().adopt(this.box);\r\n		this.addHeader(tvtArr[0]);\r\n		for (var row=1;row<tvtArr.length;row++) this.addItem(tvtArr[row]);\r\n		{$loadfile}\r\n	},\r\n	build: function(val){\r\n		return new Element(\'input\',{\'type\':\'text\',\'styles\':{\'width\':\'100px\'},\'value\':val,\'events\':{\'keyup\':function(){this.setEditor();documentDirty=true;}.bind(this)}});\r\n	},\r\n	addHeader: function(values,elem){\r\n		var rowDiv = new Element(\'div\',{\'class\':\'tvtrow\',\'styles\':{\'background\':\'#f0f0ee\',\'padding\':\'5px 0\',\'white-space\':\'nowrap\'}});\r\n		this.box.adopt(rowDiv);\r\n		if (!values) values=[\'\',\'\'];\r\n		this.cols=values.length;\r\n		for (var i=0;i<this.cols;i++) rowDiv.adopt(this.build(values[i]));\r\n		rowDiv.adopt(new Element(\'input\',{\'type\':\'button\',\'value\':\'>>\',\'events\':{\r\n			\'click\':function(){\r\n				this.cols++;\r\n				this.box.getElements(\'div.tvtrow\').each(function(item){this.build(\'\').injectAfter(item.getElements(\'input[type=text]\').getLast());}.bind(this));\r\n				this.setEditor();\r\n			}.bind(this)\r\n		}}));\r\n		rowDiv.adopt(new Element(\'input\',{\'type\':\'button\',\'value\':\'<<\',\'events\':{\r\n			\'click\':function(){\r\n				if (rowDiv.getElements(\'input[type=text]\').length>2){\r\n					this.cols--;\r\n					this.box.getElements(\'div.tvtrow\').each(function(item){item.getElements(\'input[type=text]\').getLast().remove();});\r\n					this.setEditor();\r\n				}\r\n			}.bind(this)\r\n		}}));\r\n	},\r\n	addItem: function(values,elem){\r\n		var rowDiv = new Element(\'div\',{\'class\':\'tvtrow\',\'styles\':{\'white-space\':\'nowrap\'}});\r\n		if (elem) {rowDiv.injectAfter(elem);} else {this.box.adopt(rowDiv);}\r\n		for (var i=0;i<this.cols;i++) rowDiv.adopt(this.build((values) ? values[i] : \'\'));\r\n		rowDiv.adopt(new Element(\'input\',{\'type\':\'button\',\'value\':\'+\',\'events\': {\r\n			\'click\':function(){this.addItem(null,rowDiv);}.bind(this)\r\n		}}));\r\n		if (this.box.getElements(\'div.tvtrow\').length>2) rowDiv.adopt(new Element(\'input\',{\'type\':\'button\',\'value\':\'-\',\'events\':{\r\n			\'click\':function(){rowDiv.remove();this.setEditor();}.bind(this)\r\n		}}));\r\n	},\r\n	setEditor: function(){\r\n		var tvtArr=new Array();\r\n		this.box.getElements(\'div.tvtrow\').each(function(item){\r\n			var itemsArr=new Array();\r\n			var inputs=item.getElements(\'input[type=text]\');\r\n			inputs.each(function(item){itemsArr.push(item.value);});\r\n			tvtArr.push(itemsArr);\r\n		});\r\n		this.fid.value = Json.toString(tvtArr);\r\n	}\r\n});\r\nwindow.addEvent(\'domready\', function(){\r\n	var tvIds = [$tvIds];\r\n	for (var i=0;i<tvIds.length;i++){\r\n		var fid = \'tv\'+ tvIds[i];\r\n		if($(fid)!=null) {var modxTvTable=new TvTable(fid);}\r\n	}\r\n});\r\n</script>\r\n<!-- /TvTable -->\r\nOUT;\r\n$e->output($output);\r\n}\r\nif ($e->name == \'OnBeforeDocFormSave\'){\r\n$tvIds=explode(\',\',$tvIds);\r\nforeach ($tvIds as $tvid) {\r\n	$fname=\'file_tv\'.$tvid;\r\n	if (isset($_FILES[$fname]) && is_uploaded_file($_FILES[$fname][\'tmp_name\']) && is_readable($_FILES[$fname][\'tmp_name\'])) {\r\n		$file=fopen($_FILES[$fname][\'tmp_name\'],\'r\');\r\n		while (($data = fgetcsv($file, 1000, \',\')) !== FALSE) {$dataArr[]=$data;}\r\n		fclose($file);\r\n		$tmplvars[$tvid][1]=str_replace(\'\\\\/\', \'/\', json_encode($dataArr));\r\n	}\r\n}\r\n}\r\n//?>','0','&tvIds=TV Ids;text;&templ=Template;text;&role=Role;text;&loadfile=Load csv file;list;true,false;false;','1','');
INSERT INTO `skveez_site_plugins` VALUES ('21','ManagerManager','<strong>0.3.11</strong> Customize the MODx Manager to offer bespoke admin functions for end users.','0','5','0','// You can put your ManagerManager rules EITHER in a chunk OR in an external file - whichever suits your development style the best\r\n\r\n// To use an external file, put your rules in /assets/plugins/managermanager/mm_rules.inc.php \r\n// (you can rename default.mm_rules.inc.php and use it as an example)\r\n// The chunk SHOULD have php opening tags at the beginning and end\r\n\r\n// If you want to put your rules in a chunk (so you can edit them through the Manager),\r\n// create the chunk, and enter its name in the configuration tab.\r\n// The chunk should NOT have php tags at the beginning or end\r\n\r\n// ManagerManager requires jQuery 1.3+\r\n// The URL to the jQuery library. Choose from the configuration tab whether you want to use \r\n// a local copy (which defaults to the jQuery library distributed with ModX 1.0.1)\r\n// a remote copy (which defaults to the Google Code hosted version)\r\n// or specify a URL to a custom location.\r\n// Here we set some default values, because this is a convenient place to change them if we need to,\r\n// but you should configure your preference via the Configuration tab.\r\n$js_default_url_local = $modx->config[\'site_url\']. \'/assets/js/jquery-1.7.1.min.js\';\r\n$js_default_url_remote = \'http://ajax.googleapis.com/ajax/libs/jquery/1.7.1/jquery.min.js\';\r\n\r\n// You don\'t need to change anything else from here onwards\r\n//-------------------------------------------------------\r\n\r\n// Run the main code\r\n$asset_path = $modx->config[\'base_path\'] . \'assets/plugins/managermanager/mm.inc.php\';\r\ninclude($asset_path);\r\n','0','&config_chunk=Configuration Chunk;text;mm_rules; &remove_deprecated_tv_types_pref=Remove deprecated TV types;list;yes,no;yes &which_jquery=jQuery source;list;local (assets/js),remote (google code),manual url (specify below);local (assets/js) &js_src_type=jQuery URL override;text;','0','');
INSERT INTO `skveez_site_plugins` VALUES ('22','phx','<strong>2.1.4</strong> (Placeholders Xtended) Adds the capability of output modifiers when using placeholders, template variables and settings tags','0','0','0','include_once $modx->config[\'rb_base_dir\'] . \"plugins/phx/phx.parser.class.inc.php\";\r\n\r\n$e = &$modx->Event;\r\n\r\n$PHx = new PHxParser($phxdebug,$phxmaxpass);\r\n\r\nswitch($e->name) {\r\n	case \'OnParseDocument\':\r\n		$PHx->OnParseDocument();\r\n		break;\r\n\r\n}','0','&phxdebug=Log events;int;0 &phxmaxpass=Max. Passes;int;50','1','');
INSERT INTO `skveez_site_plugins` VALUES ('23','Strict URLs','<strong>1.0.1</strong> Enforces the use of strict URLs to prevent duplicate content.','0','5','0','// For overriding documents, create a new template variabe (TV) named seoOverride with the following options:\r\n//    Input Type: DropDown List Menu\r\n//    Input Option Values: Disabled==-1||Base Name==0||Append Extension==1||Folder==2\r\n//    Default Value: -1\r\n\r\n//  # Include the following in your .htaccess file\r\n//  # Replace \"example.com\" &  \"example\\.com\" with your domain info\r\n//  RewriteCond %{HTTP_HOST} .\r\n//  RewriteCond %{HTTP_HOST} !^www\\.example\\.com [NC]\r\n//  RewriteRule (.*) http://www.example.com/$1 [R=301,L] \r\n\r\n// Begin plugin code\r\n$e = &$modx->event;\r\n\r\nif ($e->name == \'OnWebPageInit\') \r\n{\r\n   $documentIdentifier = $modx->documentIdentifier;\r\n\r\n   if ($documentIdentifier)  // Check for 404 error\r\n   {\r\n      $myProtocol = ($_SERVER[\'HTTPS\'] == \'on\') ? \'https\' : \'http\';\r\n      $s = $_SERVER[\'REQUEST_URI\'];\r\n      $parts = explode(\"?\", $s);  \r\n\r\n      $alias = $modx->aliasListing[$documentIdentifier][\'alias\'];\r\n      if ($makeFolders)\r\n      {\r\n         if ($emptyFolders)\r\n         {\r\n            $result = $modx->db->select(\'isfolder\', $modx->getFullTableName(\'site_content\'), \'id = \' . $documentIdentifier);\r\n            $isfolder = $modx->db->getValue($result);\r\n         }\r\n         else\r\n         {\r\n            $isfolder = (count($modx->getChildIds($documentIdentifier, 1)) > 0) ? 1 : 0;\r\n         }\r\n      }\r\n\r\n      if ($override && $overrideOption = $modx->getTemplateVarOutput($overrideTV, $documentIdentifier))\r\n      {\r\n         switch ($overrideOption[$overrideTV])\r\n         {\r\n            case 0:\r\n               $isoverride = 1;\r\n               break;\r\n            case 1:\r\n               $isfolder = 0;\r\n               break;\r\n            case 2:\r\n               $makeFolders = 1;\r\n               $isfolder = 1;\r\n         }\r\n      }\r\n\r\n      if ($isoverride)\r\n      {\r\n         $strictURL = preg_replace(\'/[^\\/]+$/\', $alias, $modx->makeUrl($documentIdentifier));\r\n      }\r\n      elseif ($isfolder && $makeFolders)\r\n      {\r\n         $strictURL = preg_replace(\'/[^\\/]+$/\', $alias, $modx->makeUrl($documentIdentifier)) . \"/\";\r\n      }\r\n      else\r\n      {\r\n         $strictURL = $modx->makeUrl($documentIdentifier);\r\n      }\r\n\r\n      $myDomain = $myProtocol . \"://\" . $_SERVER[\'HTTP_HOST\'];\r\n      $newURL = $myDomain . $strictURL;\r\n      $requestedURL = $myDomain . $parts[0];\r\n\r\n      if ($documentIdentifier == $modx->config[\'site_start\'])\r\n      {\r\n         if ($requestedURL != $modx->config[\'site_url\'])\r\n         {\r\n            // Force redirect of site start\r\n            header(\"HTTP/1.1 301 Moved Permanently\");\r\n            $qstring = preg_replace(\"#(^|&)(q|id)=[^&]+#\", \'\', $parts[1]);  // Strip conflicting id/q from query string\r\n            if ($qstring) header(\'Location: \' . $modx->config[\'site_url\'] . \'?\' . $qstring);\r\n            else header(\'Location: \' . $modx->config[\'site_url\']);\r\n            exit(0);\r\n         }\r\n      }\r\n      elseif ($parts[0] != $strictURL)\r\n      {\r\n         // Force page redirect\r\n         header(\"HTTP/1.1 301 Moved Permanently\");\r\n         $qstring = preg_replace(\"#(^|&)(q|id)=[^&]+#\", \'\', $parts[1]);  // Strip conflicting id/q from query string\r\n         if ($qstring) header(\'Location: \' . $strictURL . \'?\' . $qstring);\r\n         else header(\'Location: \' . $strictURL);\r\n         exit(0);\r\n      }\r\n   }\r\n}\r\nelseif ($e->name == \'OnWebPagePrerender\')\r\n{\r\n   if ($editDocLinks)\r\n   {\r\n      $myDomain = $_SERVER[\'HTTP_HOST\'];\r\n      $furlSuffix = $modx->config[\'friendly_url_suffix\'];\r\n      $baseUrl = $modx->config[\'base_url\'];\r\n      $o = &$modx->documentOutput; // get a reference of the output\r\n\r\n      // Reduce site start to base url\r\n      $overrideAlias = $modx->aliasListing[$modx->config[\'site_start\']][\'alias\'];\r\n      $overridePath = $modx->aliasListing[$modx->config[\'site_start\']][\'path\'];\r\n      $o = preg_replace(\"#((href|action)=\\\"|$myDomain)($baseUrl)?($overridePath/)?$overrideAlias$furlSuffix#\", \'${1}\' . $baseUrl, $o);\r\n\r\n      if ($override)\r\n      {\r\n         // Replace manual override links\r\n         $sql = \"SELECT tvc.contentid as id, tvc.value as value FROM \" . $modx->getFullTableName(\'site_tmplvars\') . \" tv \";\r\n         $sql .= \"INNER JOIN \" . $modx->getFullTableName(\'site_tmplvar_templates\') . \" tvtpl ON tvtpl.tmplvarid = tv.id \";\r\n         $sql .= \"LEFT JOIN \" . $modx->getFullTableName(\'site_tmplvar_contentvalues\') . \" tvc ON tvc.tmplvarid = tv.id \";\r\n         $sql .= \"LEFT JOIN \" . $modx->getFullTableName(\'site_content\') . \" sc ON sc.id = tvc.contentid \";\r\n         $sql .= \"WHERE sc.published = 1 AND tvtpl.templateid = sc.template AND tv.name = \'$overrideTV\'\";\r\n         $results = $modx->dbQuery($sql);\r\n         while ($row = $modx->fetchRow($results))\r\n         {\r\n            $overrideAlias = $modx->aliasListing[$row[\'id\']][\'alias\'];\r\n            $overridePath = $modx->aliasListing[$row[\'id\']][\'path\'];\r\n            switch ($row[\'value\'])\r\n            {\r\n               case 0:\r\n                  $o = preg_replace(\"#((href|action)=\\\"($baseUrl)?($overridePath/)?|$myDomain$baseUrl$overridePath/?)$overrideAlias$furlSuffix#\", \'${1}\' . $overrideAlias, $o);\r\n                  break;\r\n               case 2:\r\n                  $o = preg_replace(\"#((href|action)=\\\"($baseUrl)?($overridePath/)?|$myDomain$baseUrl$overridePath/?)$overrideAlias$furlSuffix/?#\", \'${1}\' . rtrim($overrideAlias, \'/\') . \'/\', $o);\r\n                  break;\r\n            }\r\n         }\r\n      }\r\n\r\n      if ($makeFolders)\r\n      {\r\n         if ($emptyFolders)\r\n         {\r\n            // Populate isfolder array\r\n            $isfolder_arr = array();\r\n            $result = $modx->db->select(\'id\', $modx->getFullTableName(\'site_content\'), \'published > 0 AND isfolder > 0\');\r\n            while ($row = $modx->db->getRow($result))\r\n               $isfolder_arr[$row[\'id\']] = true;\r\n         }\r\n\r\n         // Replace container links\r\n         foreach ($modx->documentListing as $id)\r\n         {\r\n            if ((is_array($isfolder_arr) && isset($isfolder_arr[$id])) || count($modx->getChildIds($id, 1)))\r\n            {\r\n               $overrideAlias = $modx->aliasListing[$id][\'alias\'];\r\n               $overridePath = $modx->aliasListing[$id][\'path\'];\r\n               $o = preg_replace(\"#((href|action)=\\\"($baseUrl)?($overridePath/)?|$myDomain$baseUrl$overridePath/?)$overrideAlias$furlSuffix/?#\", \'${1}\' . rtrim($overrideAlias, \'/\') . \'/\', $o);\r\n            }\r\n         }\r\n      }\r\n   }\r\n}','0','&editDocLinks=Edit document links;int;1 &makeFolders=Rewrite containers as folders;int;1 &emptyFolders=Check for empty container when rewriting;int;0 &override=Enable manual overrides;int;1 &overrideTV=Override TV name;string;seoOverride','0','');
INSERT INTO `skveez_site_plugins` VALUES ('24','TransAlias','<strong>1.0.1</strong> Human readible URL translation supporting multiple languages and overrides','0','5','0','/*\r\n * Initialize parameters\r\n */\r\nif (!isset ($alias)) { return ; }\r\nif (!isset ($plugin_dir) ) { $plugin_dir = \'transalias\'; }\r\nif (!isset ($plugin_path) ) { $plugin_path = $modx->config[\'base_path\'].\'assets/plugins/\'.$plugin_dir; }\r\nif (!isset ($table_name)) { $table_name = \'common\'; }\r\nif (!isset ($char_restrict)) { $char_restrict = \'lowercase alphanumeric\'; }\r\nif (!isset ($remove_periods)) { $remove_periods = \'No\'; }\r\nif (!isset ($word_separator)) { $word_separator = \'dash\'; }\r\nif (!isset ($override_tv)) { $override_tv = \'\'; }\r\n\r\nif (!class_exists(\'TransAlias\')) {\r\n    require_once $plugin_path.\'/transalias.class.php\';\r\n}\r\n$trans = new TransAlias($modx);\r\n\r\n/*\r\n * see if TV overrides the table name\r\n */\r\nif(!empty($override_tv)) {\r\n    $tvval = $trans->getTVValue($override_tv);\r\n    if(!empty($tvval)) {\r\n        $table_name = $tvval;\r\n    }\r\n}\r\n\r\n/*\r\n * Handle events\r\n */\r\n$e =& $modx->event;\r\nswitch ($e->name ) {\r\n    case \'OnStripAlias\':\r\n        if ($trans->loadTable($table_name, $remove_periods)) {\r\n            $output = $trans->stripAlias($alias,$char_restrict,$word_separator);\r\n            $e->output($output);\r\n            $e->stopPropagation();\r\n        }\r\n        break ;\r\n    default:\r\n        return ;\r\n}','0','&table_name=Trans table;list;common,russian,utf8,utf8lowercase;russian &char_restrict=Restrict alias to;list;lowercase alphanumeric,alphanumeric,legal characters;lowercase alphanumeric &remove_periods=Remove Periods;list;Yes,No;No &word_separator=Word Separator;list;dash,underscore,none;dash &override_tv=Override TV name;string;','0','');

